{
    // Find the pending game for the userTeam
    FindPendingGameView.Subjects = PendingGameList;
    FindPendingGameView.FilterTeam = team;
    FindPendingGameView.Update();

    // Retrieve the pending game for this team.
    PendingSeasonGame[] pendingGameList = FindPendingGameView.Results;
    if (pendingGameList != null) 
    {
        if (pendingGameList.Population() > 0)
        {
            PendingSeasonGame pendingGame = pendingGameList[0];
            SeasonGame seasonGame = pendingGame.SeasonGame;

            if(!seasonGame.HasBeenPublished && seasonGame.IsSimmed && (seasonGame.GameStatus != GameStatus.Unplayed))
            {
                // Do this first because the function runs its own validation before clearing the cache
                StatManager.ClearPresimCache(seasonGame);

                seasonGame.GameStatus = GameStatus.Unplayed;
    // Note: this line only works here because we're not invalidating CPU vs. CPU games
                seasonGame.IsHumanFlag = true;
                seasonGame.HomeScore = 0;
                seasonGame.HomeScoreOT = 0;
                seasonGame.HomeScoreQuarter1 = 0;
                seasonGame.HomeScoreQuarter2 = 0;
                seasonGame.HomeScoreQuarter3 = 0;
                seasonGame.HomeScoreQuarter4 = 0;
                seasonGame.AwayScore = 0;
                seasonGame.AwayScoreOT = 0;
                seasonGame.AwayScoreQuarter1 = 0;
                seasonGame.AwayScoreQuarter2 = 0;
                seasonGame.AwayScoreQuarter3 = 0;
                seasonGame.AwayScoreQuarter4 = 0;
                seasonGame.HomeTeamStatus = GameTeamStatus.Pending;
                seasonGame.AwayTeamStatus = GameTeamStatus.Pending;
                seasonGame.IsSimmed = false;
                seasonGame.IsOvertimeGame = false;
                 seasonGame.QuarterLengthMins = 15;
            }

        }
    }
