// If the user is valid, proceed.
if (user != null)
{
    Team userTeam = user.Team;

    // If the advance transaction is not currently advancing, determine if a seaosn game is pending.
    if (!IsAdvancing && (PendingGameList != null))
    {
        FindPendingGameView.Subjects = PendingGameList;
        FindPendingGameView.FilterTeam = userTeam;
        FindPendingGameView.Update();

        // Retrieve the pending game for this team.
       PendingSeasonGame[] pendingGameList = FindPendingGameView.Results;
        if (pendingGameList != null) {
        if (pendingGameList.Population() > 0)
        {
            PendingSeasonGame pendingGame = pendingGameList[0];
            SeasonGame seasonGame = pendingGame.SeasonGame;

            if (userTeam == seasonGame.HomeTeam)
            {
                if(pendingGame.HomeTeamRequest == null)
                {
                   {
                     // Issue the new request to the user character.
                     Request request = SeasonGameFlow.CreateGameRequest(seasonGame);
                     request.Issue(user.UserEntity);
                     pendingGame.HomeTeamRequest = request;
                     seasonGame.HomeRequestId = request.RequestId;
                     // if the game is complete then override the resolution to dismissed
                     if(SeasonGameFlow.IsGameComplete(seasonGame))
                     {
                         request.Resolution = 3; // dismiss 
                      }
                   }
                }
                else
                {
                   if(pendingGame.HomeTeamRequest.TargetUser == user)
                   {                     
                     {
                       pendingGame.HomeTeamRequest.ClearResponse();
                     } 
                   }
                   else
                   {
                     {
                       pendingGame.HomeTeamRequest = null;
                       Request request = SeasonGameFlow.CreateGameRequest(seasonGame);
                       request.Issue(user.UserEntity);
                       pendingGame.HomeTeamRequest = request;
                       seasonGame.HomeRequestId = request.RequestId;
                       // if the game is complete then override the resolution to dismissed
                       if(SeasonGameFlow.IsGameComplete(seasonGame))
                       {
                           request.Resolution = 3; // dismiss 
                        }
                     }
                   }
                }  

                // Refresh the opposing Team's Request
                if(pendingGame.AwayTeamRequest != null)
                {
                    if (pendingGame.AwayTeamRequest.RequestId != -1)
                    {
                        pendingGame.AwayTeamRequest.Refresh();
                    }
                }
            }
            else if (userTeam == seasonGame.AwayTeam)
            {
                if(pendingGame.AwayTeamRequest == null)
                {
                   {
                     // Issue the new request to the user character.
                     Request request = SeasonGameFlow.CreateGameRequest(seasonGame);
                     request.Issue(user.UserEntity);
                     pendingGame.AwayTeamRequest = request;
                     seasonGame.AwayRequestId = request.RequestId;
                     // if the game is complete then override the resolution to dismissed
                     if(SeasonGameFlow.IsGameComplete(seasonGame))
                     {
                         request.Resolution = 3; // dismiss 
                      }
                   }
                } 
                else
                {
                   if(pendingGame.AwayTeamRequest.TargetUser == user)
                   {                     
                     {
                       pendingGame.AwayTeamRequest.ClearResponse();
                     }
                   }
                   else
                   {
                     {
                       pendingGame.AwayTeamRequest = null;
                       Request request = SeasonGameFlow.CreateGameRequest(seasonGame);
                       request.Issue(user.UserEntity);
                       pendingGame.AwayTeamRequest = request;
                       seasonGame.AwayRequestId = request.RequestId;
                       // if the game is complete then override the resolution to dismissed
                       if(SeasonGameFlow.IsGameComplete(seasonGame))
                       {
                           request.Resolution = 3; // dismiss 
                        }
                     }
                   }
                }

                // Refresh the opposing Team's Request
                if(pendingGame.HomeTeamRequest != null )
                {
                    if (pendingGame.HomeTeamRequest.RequestId != -1)
                    {
                           pendingGame.HomeTeamRequest.Refresh();
                    }
                }
            }   
        }
        }

        FindPendingGameView.Clear();
    }
}

