// an offline multi-user league, all users will respond. Only the first response should be handled.
if ((Response != null) && (DraftTransaction.CurrentStage == null))
{
    // If the "Start" response was selected, start the draft.
    if (Response == StartResponse)
    {
        DraftTransaction.SetSimState(DraftSimState.Live);
        DraftTransaction.Start();
    }
    // If the "Sim" response was selected, sim the draft.
    else if (Response == SimResponse)
    {
        DraftTransaction.SetSimState(DraftSimState.SimToEnd);
        DraftTransaction.Start();
    }
}

