TrueTalentComparison comparison = TrueTalentComparison.GoodPick;

int totalRank = ((RoundPicked) * 32) + NumberPicked;
int trueRank = ((TrueTalentRound) * 32) + TrueTalentNumber;

int rankDiff = totalRank - trueRank;

if (RoundPicked <= 3 && rankDiff <= -50)
{
    comparison = TrueTalentComparison.Reached;
}
else if (rankDiff >= 50)
{
    comparison = TrueTalentComparison.ExcellentPick;
}

comparison;

int strength = draftPlayer.Player.StrengthRating;

//There's no way currently to cast between int's and floats
//So we have to multiply the initial value of each part by 10,000, then divide by that at the end
//0.0079 * 10,000; 0.4028 * 10,000; 8.7735 * 10,000
//int reps = (0.0079 * (strength * strength)) - (0.4028 * strength) + 8.7735;
int reps = ((79 * (strength * strength)) - (4028 * strength) + 87735) / 10000;

int finalReps = reps + RandomRef.GetRandomIntRange(-2,2);

//Clamp the value
reps = ClampValue(finalReps, 4, 46);

reps;

int jump = draftPlayer.Player.JumpingRating;
int strength = draftPlayer.Player.StrengthRating;

//There's no way currently to cast between int's and floats
//So we have to multiply the initial value of each part by 1,000, then divide by that at the end
//DistanceInInches = (.554)(.7*Jumping + .5*Strength) + 68.156
int inches = (((554 * ((700 * jump) + (500 * strength)))/1000) + 68156) / 1000;

int inchesFinal = inches + RandomRef.GetRandomIntRange(-5,5);

//Clamp the value
inches = ClampValue(inchesFinal, 87, 134);

//returns inches
inches;

int speed = draftPlayer.Player.SpeedRating;


//There's no way currently to cast between int's and floats
//this function would always equal 6 because 27/980 = 0, effectively ignoring speed 
//So we have to multiply the initial value of each part by 10,000, then divide by that at the end
//int time = (34141 / 4900) - (27/980 * speed);
int time = ((((34141 *10000) / 4900) - ((27 * 10000)/980) * (speed + ((speed * RandomRef.GetRandomIntRange(-3,3))/100) )  ) / 100) + RandomRef.GetRandomIntRange(-1,1);

//Clamp the value
time = ClampValue(time, 424, 559);

//returns time in seconds x 100 (10000 / 100 = 100)
//otherwise we would be truncating the fractional part
//ex: 4.24 seconds = 424

time;

int speed = draftPlayer.Player.SpeedRating;
int acceleration = draftPlayer.Player.AccelerationRating;
int agility = draftPlayer.Player.AgilityRating;
int strength = draftPlayer.Player.StrengthRating;
int jump = draftPlayer.Player.JumpingRating;

//COMBINE_TOTAL = SPEED + ACCELERATION + AGILITY + 2*STRENGTH + JUMPING
int combineTotal = speed + acceleration + agility + (2 * strength) + jump;

//There's no way currently to cast between int's and floats
//So we have to multiply the initial value of each part by 10,000, then divide by that at the end
//COMBINE GRADE = 0.0304*COMBINE_TOTAL - 8.0547
int grade = ((304 * combineTotal) - 80547) / 100;

//Clamp the value
grade = ClampValue(grade, 100, 1000);

//returns grade x 100 (10000 / 100 = 100)
//otherwise we would be truncating the fractional part
//ex: 7.30 = 730
grade;

draftPlayer.CombineFortyYardDash = CalculateCombineFortyYardDash(draftPlayer);

draftPlayer.CombineBenchPress = CalculateCombineBenchPress(draftPlayer);

draftPlayer.CombineVerticalJump = CalculateCombineVerticalJump(draftPlayer);

draftPlayer.CombineBroadJump = CalculateCombineBroadJump(draftPlayer);

draftPlayer.CombineThreeConeDrill = CalculateCombineThreeConeDrill(draftPlayer);

draftPlayer.CombineTwentyYardShuttle = CalculateCombineTwentyYardShuttle(draftPlayer);

draftPlayer.CombineOverallGrade = CalculateCombineOverallGrade(draftPlayer);

int agility = draftPlayer.Player.AgilityRating;
int acceleration = draftPlayer.Player.AccelerationRating;

//There's no way currently to cast between int's and floats
//So we have to multiply the initial value of each part by 10,000, then divide by that at the end
//TimeInSec= (-0.0464)(.4*Agility + .6*Acceleration) + 11.066 
int seconds = (((-464* ((4000 * agility) + (6000 * acceleration)))/10000) + 110660) / 100;

//add some randomness
int secondsFinal = seconds + RandomRef.GetRandomIntRange(-1,1);

//Clamp the value
seconds = ClampValue(secondsFinal, 647, 829);

//returns time in seconds x 100 (10000 / 100 = 100)
//otherwise we would be truncating the fractional part
//ex: 6.42 seconds = 642
seconds;

int agility = draftPlayer.Player.AgilityRating;
int acceleration = draftPlayer.Player.AccelerationRating;

//There's no way currently to cast between int's and floats
//So we have to multiply the initial value of each part by 10,000, then divide by that at the end
//TimeInSec= (-0.0349)(.6*Agility + .4*Acceleration) + 7.2679  
int seconds = (((-349* ((6000 * agility) + (4000 * acceleration)))/10000) + 72679) / 100;

//randomness
int secondsFinal = seconds + RandomRef.GetRandomIntRange(-1,1);


//Clamp the value
seconds = ClampValue(secondsFinal, 381, 527);

//returns time in seconds x 100 (10000 / 100 = 100)
//otherwise we would be truncating the fractional part
//ex: 3.81 seconds = 381
seconds;

int jump = draftPlayer.Player.JumpingRating;

//There's no way currently to cast between int's and floats
//So we have to multiply the initial value of each part by 10,000, then divide by that at the end
//DistanceInInches = (.0045* JUMPING^2) - (0.2436*JUMPING) + 23.655
int inches = ((45 * (jump * jump)) - (2436 * jump) + 236550) / 1000;

int inchesFinal = inches + RandomRef.GetRandomIntRange(-5,5);

//Clamp the value
inches = ClampValue(inchesFinal, 205, 436);

//returns inches x 10 (10000 / 1000 = 10)
//otherwise we would be truncating the fractional part
//ex: 20.5 inches = 205
inches;

int newValue = valueIn;

if (newValue < valueMin)
{
 newValue = valueMin;
}

if (newValue > valueMax)
{
 newValue = valueMax;
}

newValue;

request.RequestData = null;
request.SelectedPlayer = null;
request.Response = null;

int pickTime = 0;

// Initialize the available responses.
request.ResponseForms.EraseAll();
request.DefaultResponse = request.DelegateResponse;

// If the draft pick is valid, this is the normal draft.
if (pick != null)
{
    request.ResponseForms.Append(request.SelectPlayerResponse);
    request.ResponseForms.Append(request.DelegateResponse);

    pickTime = DraftManager.PickTimePerRound[pick.Round];

}
// Otherwise, this is the fantasy draft. There is no delegate response.
else
{
    request.ResponseForms.Append(request.SelectPlayerResponse);

    pickTime = DraftManager.FantasyPickTimePerStage[DraftManager.DraftInfo.CurrentStageIndex];
}

// Set the request data.
request.RequestData = team;
request.ExpirationDuration = pickTime;
request.CpuResponseMinDuration = pickTime - 5;
request.CpuResponseMaxDuration = pickTime - 5;

// Clear the selected player.
request.SelectedPlayer = null;
request.OverallPickIndex = pickIndex;

// Return the request record.
request;

DraftPlayer draftPlayer = new DraftPlayer;

// Get current pick info
DraftInfo draftInfo = DraftManager.DraftInfo;

int MAX_ROUND = 7;

// During the last Y rounds we choose the player at the top of the Draft Board.
if (draftInfo.CurrentRoundIndex + 1 > MAX_ROUND - LastRoundsCount)
{
   draftPlayer = GetTopOfTheDraftBoardPlayer(team);
}
else
{
  draftPlayer = GetUndraftedPlayer(team, true);

  if (draftPlayer == null)
  {
    draftPlayer = GetOldestDraftedPlayer(team);
  }

  if (draftPlayer == null)
  {
    draftPlayer = GetUndraftedPlayer(team, false);
  }
        
  if (draftPlayer == null)
  {
    draftPlayer = GetPlayerPerUnreachedMaxPosition(team, false);
  }
}

draftPlayer;

bool isDraftActive = false;

// If this is the normal draft, proceed as normal.
if (!DraftInfo.IsFantasyDraft)
{
    // Initialize the next pick.
    int nextPickIndex = DraftInfo.CurrentPickIndex+1;
    DraftPick currPick = DraftInfo.CurrentPick;
    DraftPick nextPick = null;
    Team nextTeam = null;

    // If there are remaining picks, fetch the next one.
    if (nextPickIndex < DraftPickOrder.Population())
    {
        nextPick = DraftPickOrder[nextPickIndex];
        nextTeam = nextPick.CurrentTeam;
    }

    // Send the end pick event.
    EndPickEvent.Pick = currPick;
    EndPickEvent.Team = currPick.CurrentTeam;
    EndPickEvent.NextTeam = nextTeam;
    EndPickEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
    EndPickEvent.Send();

    // If there are remaining picks, advance the pick.
    if (nextPick != null)
    {
        // If this is the last pick of the round, end the current round and begin a new one.
        if (nextPick.Round != currPick.Round)
        {
            // Send the end round event.
            EndRoundEvent.Round = currPick.Round;
            EndRoundEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
            EndRoundEvent.Send();

            // Advance the round.
            DraftInfo.CurrentRoundIndex = nextPick.Round;
            DraftInfo.CurrentRoundPickIndex = 0;

            // Send the begin round event.
            BeginRoundEvent.Round = nextPick.Round;
            BeginRoundEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
            BeginRoundEvent.Send();
        }
        // Otherwise, increment the current round pick index.
        else
        {
            DraftInfo.CurrentRoundPickIndex = DraftInfo.CurrentRoundPickIndex + 1;
        }

        // Advance the pick.
        DraftInfo.CurrentPickIndex = nextPickIndex;
        DraftInfo.CurrentPick = nextPick;
        DraftInfo.CurrentTeam = nextTeam;

        // Send the begin pick event.
        BeginPickEvent.Pick = DraftInfo.CurrentPick;
        BeginPickEvent.PickTime = PickTimePerRound[DraftInfo.CurrentRoundIndex];
        BeginPickEvent.Team = DraftInfo.CurrentTeam;
        BeginPickEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
        BeginPickEvent.Send();
    }
    // Otherwise, end the round. The draft will end subsequently.
    else
    {
        EndRoundEvent.Round = currPick.Round;
        EndRoundEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
        EndRoundEvent.Send();
    }

    // Return true if there are remaining picks.
    isDraftActive = (nextPick != null);
}
// Otherwise, this is the fantasy draft. Advance based on the team ordering.
else
{
    // The fantasy draft consists of 9 stages of 6 rounds each, with 32 picks per round.
    int stageCount = FantasyDraft.StageCount;
    int stageRoundCount = FantasyDraft.StageRoundCount;
    int roundPickCount = Franchise.Teams.Population();

    // Determine the current and next stage, round, pick, and team.
    int currStageIndex = DraftInfo.CurrentStageIndex;
    int nextStageIndex = DraftInfo.CurrentStageIndex;
    int currRoundIndex = DraftInfo.CurrentRoundIndex;
    int nextRoundIndex = DraftInfo.CurrentRoundIndex;
    int currRoundPickIndex = DraftInfo.CurrentRoundPickIndex;
    int nextRoundPickIndex = DraftInfo.CurrentRoundPickIndex + 1;
    int currPickIndex = DraftInfo.CurrentPickIndex;
    int nextPickIndex = DraftInfo.CurrentPickIndex + 1;
    Team currTeam = DraftInfo.CurrentTeam;
    Team nextTeam = null;

    // If this is a new round, increment it and reset the pick index.
    if (nextRoundPickIndex >= roundPickCount)
    {
        nextRoundIndex = currRoundIndex + 1;
        nextRoundPickIndex = 0;
    }

    // If this is a new stage, increment it and reset the round index.
    if (nextRoundIndex >= stageRoundCount)
    {
        nextStageIndex = currStageIndex + 1;
        nextRoundIndex = 0;
        nextPickIndex = 0;
    }

    // If there are picks remaining, determine the next team.
    if (nextStageIndex < stageCount)
    {
        nextTeam = FantasyDraft.GetCurrentTeam(nextRoundIndex, nextRoundPickIndex, Franchise.Teams);
    }

    // Send the end pick event.
    EndPickEvent.Pick = null;
    EndPickEvent.Team = currTeam;
    EndPickEvent.NextTeam = nextTeam;
    EndPickEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
    EndPickEvent.Send();

    // If there are remaining picks, advance the pick.
    if (nextStageIndex < stageCount)
    {
        // If this is the last pick of the round, end the current round and begin a new one.
        if (nextRoundIndex != currRoundIndex)
        {
            // Send the end round event.
            EndRoundEvent.Round = currRoundIndex;
            EndRoundEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
            EndRoundEvent.Send();

            // If a new stage is beginning, end this stage of the draft.
            if (nextStageIndex != currStageIndex)
            {
                EndStageEvent.IsFantasyDraft = true;
                EndStageEvent.Stage = currStageIndex;
                EndStageEvent.Send();
            }

            // Advance the pick.
            DraftInfo.CurrentStageIndex = nextStageIndex;
            DraftInfo.CurrentRoundIndex = nextRoundIndex;
            DraftInfo.CurrentRoundPickIndex = nextRoundPickIndex;
            DraftInfo.CurrentPickIndex = nextPickIndex;
            DraftInfo.CurrentTeam = nextTeam;

            // If a new stage is beginning, begin the new stage.
            if (nextStageIndex != currStageIndex)
            {
                BeginStageEvent.IsFantasyDraft = true;
                BeginStageEvent.Stage = nextStageIndex;
                BeginStageEvent.Send();
            }

            // Send the begin round event.
            BeginRoundEvent.Round = nextRoundIndex;
            BeginRoundEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
            BeginRoundEvent.Send();
        }
        // Otherwise, advance the pick only.
        else
        {
            DraftInfo.CurrentRoundPickIndex = nextRoundPickIndex;
            DraftInfo.CurrentPickIndex = nextPickIndex;
            DraftInfo.CurrentTeam = nextTeam;
        }

        // Send the begin pick event.
        BeginPickEvent.Pick = null;
        BeginPickEvent.PickTime = FantasyPickTimePerStage[DraftInfo.CurrentStageIndex];
        BeginPickEvent.Team = DraftInfo.CurrentTeam;
        BeginPickEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
        BeginPickEvent.Send();
    }
    // Otherwise, end the round. The draft will end subsequently.
    else
    {
        EndRoundEvent.Round = currRoundIndex;
        EndRoundEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
        EndRoundEvent.Send();
    }

    // Return true if there are remaining picks.
    isDraftActive = (nextStageIndex < stageCount);
}

isDraftActive;

// Set the draft as in progress.
DraftInfo.IsStarted = true;
DraftInfo.IsComplete = false;
DraftInfo.IsFantasyDraft = isMockDraft;
DraftInfo.CurrentStageIndex = 0;
DraftInfo.CurrentRoundIndex = 0;
DraftInfo.CurrentRoundPickIndex = 0;
DraftInfo.CurrentPickIndex = 0;

// If this is the normal draft, fetch the draft pick order.
if (!DraftInfo.IsFantasyDraft)
{
    // Retrieve the draft pick order.
    GetPickOrderView.Subjects = DraftPicks;
    GetPickOrderView.Update();
    DraftPickOrder = GetPickOrderView.Results;
    GetPickOrderView.Clear();

    // Set the current pick information.
    DraftInfo.CurrentPick = DraftPickOrder[0];
    DraftInfo.CurrentTeam = DraftPickOrder[0].CurrentTeam;
}
// Otherwise, fetch the first team in the teams array.
else
{
    DraftInfo.CurrentPick = null;
    DraftInfo.CurrentTeam = FantasyDraft.GetCurrentTeam(0, 0, Franchise.Teams);
}

// Send the BeginDraft event.
BeginDraftEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
BeginDraftEvent.Send();

// Send the BeginStage event.
if (DraftInfo.IsFantasyDraft)
{
    BeginStageEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
    BeginStageEvent.Stage = 0;
    BeginStageEvent.Send();
}

// Send the BeginRound event.
BeginRoundEvent.Round = DraftInfo.CurrentRoundIndex;
BeginRoundEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
BeginRoundEvent.Send();

// Send the BeginPick event.
BeginPickEvent.Pick = DraftInfo.CurrentPick;
BeginPickEvent.Team = DraftInfo.CurrentTeam;
BeginPickEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;

// If this is the normal draft, use the pick time per round.
if (!DraftInfo.IsFantasyDraft)
{
    BeginPickEvent.PickTime = PickTimePerRound[DraftInfo.CurrentRoundIndex];
}
// Otherwise, use the fantasy draft time.
else
{
    BeginPickEvent.PickTime = FantasyPickTimePerStage[DraftInfo.CurrentStageIndex];
}

BeginPickEvent.Send();

// Initialize the draft info for the current year.
DraftInfo.IsStarted = false;
DraftInfo.IsComplete = false;
DraftInfo.IsLive = false;
DraftInfo.IsPaused = false;

Draft_ReCapDraftRequest request = new Draft_ReCapDraftRequest;

request.IsResponseRequired = false;
request.IsConnectionRequired = false;
request.IsBlocking = false;
request.IsPersistent = true;
request.IsResponseRequired = false;

request.ResponseForms = new ResponseForm[1];
{
   request.ResponseForms.Append( DraftFlowForm );
}

request;

// Debug.Assert(team == DraftInfo.CurrentTeam);
// Debug.Assert(player != null);

// If this is not the mock draft, move the player to his new team.
if (!DraftInfo.IsFantasyDraft)
{
    // Remove the player from the draft class.
    RemovePlayerFromDraftBoard(Franchise.DraftClassPlayers,draftPlayer.Player);

    // Add the player to the drafting team.
    TeamManager.DraftPlayer(team, draftPlayer.Player, DraftInfo.CurrentRoundIndex,DraftInfo.CurrentPickIndex);

    // Set the player's draft year.
    draftPlayer.Player.YearDrafted = SeasonInfo.CurrentYear+1;

    // Set the selected player on the current pick.
    DraftInfo.CurrentPick.SelectedPlayer = draftPlayer.Player;
}
// Otherwise, this is the fantasy draft. Sign the player to a default contract.
else
{
    Player player = draftPlayer.Player;

    // Determine the player's draft round and pick.
    int stageIndex = DraftInfo.CurrentStageIndex;
    int roundIndex = (stageIndex * FantasyDraft.StageRoundCount) + DraftInfo.CurrentRoundIndex;
    int roundPickIndex = DraftInfo.CurrentRoundPickIndex;

    // Set the player's round and draft pick.
    player.PLYR_DRAFTROUND = roundIndex + 1;
    player.PLYR_DRAFTPICK = roundPickIndex + 1;

    // Create a contract for this player based on his draft round.
    PlayerContract contract = new PlayerContract;
    contract.SalaryTable = new int[8];
    contract.BonusTable = new int[8];
    FantasyDraft.CreateContract(player, roundIndex, contract);

    // Add the player to the drafted player array.
    team.DraftedPlayers.Append(player);

    // Sign the player.
    TeamManager.SignPlayer(team, player, contract);

    // Reset the team balance. Player salaries should not affect finances during the fantasy draft.
    team.CurrentBalance = team.Owner.StartingBalance;
}

// Send the draft player event.
DraftPlayerEvent.Team = team;
DraftPlayerEvent.Player = draftPlayer.Player;
DraftPlayerEvent.TrueOverallRanking = draftPlayer.TrueOverallRanking;
DraftPlayerEvent.Pick = DraftInfo.CurrentPick;
DraftPlayerEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
DraftPlayerEvent.AudioQueue = draftPlayer.AudioQueue;
DraftPlayerEvent.SurnameAudioID = draftPlayer.SurnameAudioID;
DraftPlayerEvent.PlayersProjectedPick = draftPlayer.Player.PLYR_DRAFTPICK;
DraftPlayerEvent.PlayersProjectedRound = draftPlayer.Player.PLYR_DRAFTROUND;
DraftPlayerEvent.Send();

// If this is the real draft, set it as complete.
if (!DraftInfo.IsFantasyDraft)
{
    DraftInfo.IsComplete = true;
}
// Otherwise, reset it as incomplete.
else
{
    DraftInfo.IsStarted = false;
    DraftInfo.IsComplete = false;
    DraftInfo.IsFantasyDraftComplete = true;
}

// Clear the pick order.
DraftPickOrder = null;

// Send the EndStage event.
if (DraftInfo.IsFantasyDraft)
{
    EndStageEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
    EndStageEvent.Stage = DraftInfo.CurrentStageIndex;
    EndStageEvent.Send();
}

// Send the EndDraft event.
EndDraftEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
EndDraftEvent.Send();

// Clear the current pick information.
DraftInfo.CurrentStageIndex = 0;
DraftInfo.CurrentRoundIndex = 0;
DraftInfo.CurrentRoundPickIndex = 0;
DraftInfo.CurrentPickIndex = 0;
DraftInfo.CurrentPick = null;
DraftInfo.CurrentTeam = null;

// If this is not the fantasy draft, release undrafted players to free agency.
if (!DraftInfo.IsFantasyDraft)
{
    SetUndraftedPlayersToFreeAgents dataFlow = new SetUndraftedPlayersToFreeAgents;
    dataFlow.Subjects = Franchise.DraftClassPlayers;
    dataFlow.PlayerManagerRef = PlayerManager;
    dataFlow.FreeAgents = Franchise.FreeAgents;
    dataFlow.Update();
}

// Clear the fantasy draft flag.
DraftInfo.IsFantasyDraft = false;

DraftPick nextPick = null;

int nextPickIndex = DraftInfo.CurrentPickIndex + 1;
if (nextPickIndex >= 1 && nextPickIndex < DraftPickOrder.Population())
{
    nextPick = DraftPickOrder[nextPickIndex];
}

nextPick;

// Convert the round and pick to zero-based indexes.
int roundIndex = player.PLYR_DRAFTROUND - 1;
int pickIndex = player.PLYR_DRAFTPICK - 1;

// Calculate the overall pick from the round and pick number.
int overallPick = (roundIndex * GetPicksPerRound()) + pickIndex;

// If the pick is invalid, clamp it to an invalid value.
if (overallPick < 0)
{
    overallPick = -1;
}

overallPick;

// The pick has been traded if the current pick's team does not match the current team.
bool isPickTraded = (DraftInfo.CurrentPick.CurrentTeam != DraftInfo.CurrentTeam);

// If the pick was traded, send an event.
if (isPickTraded)
{
    TradeCurrentPickEvent.IsFantasyDraft = DraftInfo.IsFantasyDraft;
    TradeCurrentPickEvent.OldTeam = DraftInfo.CurrentTeam;
    TradeCurrentPickEvent.NewTeam = DraftInfo.CurrentPick.CurrentTeam;
    TradeCurrentPickEvent.Pick = DraftInfo.CurrentPick;
    TradeCurrentPickEvent.PackageA = packageA;
    TradeCurrentPickEvent.PackageB = packageB;
    TradeCurrentPickEvent.Send();
    TradeCurrentPickEvent.Clear();
}

// Update the current draft team.
DraftInfo.CurrentTeam = DraftInfo.CurrentPick.CurrentTeam;

// Return true if the pick was traded.
isPickTraded;

// Set the draft as paused.
DraftInfo.IsPaused = true;

// Send the pause event.
PauseDraftEvent.Send();

// Set the draft as not paused.
DraftInfo.IsPaused = false;

// Send the resume event.
ResumeDraftEvent.Send();

Draft_ReCapDraftRequest request = DraftManager.CreateDraftReCapRequest();

DraftReCapData draftReCapData = new DraftReCapData;
draftReCapData.SeasonInfo = DraftManager.Franchise.SeasonInfo;

request.RequestData = draftReCapData;

request.Issue( Subject.UserCharacter );

FranchiseUser newUser = event.User;
Team newUserTeam = newUser.Team;
Team currentTeam = DraftInfo.CurrentTeam;

if(DraftInfo.IsLive && !DraftInfo.IsFantasyDraft && currentTeam == newUserTeam)
{
    Request.Dismiss();
    // Create the draft pick request for the current team.
    Request = DraftEval.InitDraftPickRequest(DraftInfo.CurrentTeam, DraftInfo.CurrentPick, Request, DraftInfo.CurrentPickIndex);
    
   // Issue the draft pick request.
   TeamRequestManager.Issue(DraftInfo.CurrentTeam, Request);

   // If the pick was not already traded, begin a trade negotiation for this pick.
   if (!IsDraftPickTraded)
   {
 // Clear the outstanding trade negotiation.
       if (TradeNegotiation != null)
       {
          TradeNegotiation.Response = TradeResponse.Decline;
          TradeNegotiationManager.EndTradeNegotiation(TradeNegotiation);
          TradeNegotiation = null;
       }
       TradeNegotiation = TradeNegotiationManager.BeginDraftPickTradeNegotiation(DraftInfo.CurrentTeam, DraftInfo.CurrentPick);
   }
}

// If the normal draft is active, check if the current pick was traded.
if (!DraftInfo.IsFantasyDraft && DraftInfo.IsStarted && !DraftInfo.IsComplete)
{
    DraftPick currentPick = DraftInfo.CurrentPick;

    // If the current pick is valid, check if it is included in the trade. If so, we need to
    // transition the draft flow to the new team.
    if (currentPick != null)
    {
        TeamTradePackage packageA = event.PackageA;
        TeamTradePackage packageB = event.PackageB;
    
        // If either package contains the current pick, store the external trade.
        if ((packageA.Entity1 == currentPick) ||
            (packageA.Entity2 == currentPick) ||
            (packageA.Entity3 == currentPick) ||
            (packageB.Entity1 == currentPick) ||
            (packageB.Entity2 == currentPick) ||
            (packageB.Entity3 == currentPick))
        {
            TradePackageA = packageA;
            TradePackageB = packageB;
        }
    }
}

// If the advance request has been resolved, set the sim state accordingly.
if (AdvanceRequest.HasResolution())
{
    // Only change SimState if it is not advancing.
    if (SimState == DraftSimState.Live)
    {
        // Set the appropriate sim state.
        if (AdvanceRequest.Response == AdvanceRequest.NextPickResponse)
        {
            SetSimState(DraftSimState.SimToNextPick);
        }
        else if (AdvanceRequest.Response == AdvanceRequest.NextUserPickResponse)
        {
            SetSimState(DraftSimState.SimToNextUserPick);
        }
        else if (AdvanceRequest.Response == AdvanceRequest.NextRoundResponse)
        {
            SetSimState(DraftSimState.SimToNextRound);
        }
        else if (AdvanceRequest.Response == AdvanceRequest.EndDraftResponse)
        {
            SetSimState(DraftSimState.SimToEnd);
        }
    }
    // Clear the response.
    AdvanceRequest.Response = null;
}

// If the pause/resume request has been resolved, set the pause/resume state accordingly.
if (PauseResumeRequest != null)
{
    if (PauseResumeRequest.HasResolution())
    {
        // If pause was selected, pause and send the resume request.
        if (PauseResumeRequest == PauseRequest)
        {
            DraftManager.PauseDraft();
            IsBlocking = true;

            PauseResumeRequest = ResumeRequest;
            PauseResumeRequest.Issue(Franchise.LeagueOwner);
        }
        // If resume was selected, resume and send the pause request.
        else if (PauseResumeRequest == ResumeRequest)
        {
            DraftManager.ResumeDraft();

            // If the simulation state is live, resume the live draft.
            if (SimState == DraftSimState.Live)
            {
                PauseResumeRequest = PauseRequest;
                PauseResumeRequest.Issue(Franchise.LeagueOwner);
                IsBlocking = false;
            }
        }
    }
}

// Set the sim state.
SimState = state;
DraftInfo.DraftSimState = state;

// If the draft is now live, set to real-time.
if (state == DraftSimState.Live)
{
    TimeScale = 1.0;
    IsBlocking = DraftInfo.IsPaused;
    DraftInfo.IsLive = true;
}
// Otherwise, set to sim time and block.
else
{
    TimeScale = 0.0;
    IsBlocking = true;
    DraftInfo.IsLive = false;
}

// Enable trade notifications only when the draft is live.
TradeNegotiationManager.IsNotificationEnabled = (state == DraftSimState.Live);

Player player = Subject.Player;
PositionE position = player.Position;

int baseInterestThreshold  = 0;
PlayerPosition playerPosition = Env.PlayerManagerRef.MapEnumToPlayerPosition(position) ;
baseInterestThreshold = ( playerPosition.POSITION_IMPORTANCE  * player.GetBalancedOverallGrade()) / 100;

int stageMultiplier = 1;
int interestMultiplier = 10;

stageMultiplier = Env.FranchiseRef.SeasonInfo.CurrentFreeAgencyStage - 1;
interestMultiplier = Env.PlayerSigningEvalRef.FreeAgentInterestMultiplier;

int interestThreshold = 0;
int multiplier = 100 - (interestMultiplier * stageMultiplier);

interestThreshold = (baseInterestThreshold * multiplier) / 100;

if(Subject.PlayerInterest < interestThreshold)
{
  Subject.Declined = true;
}

Subject.PlayerInterest >= interestThreshold;

if(Subject.IsUserControlled)
{
  EvaluateContractOffersRequest request = PlayerSigningEvalRef.CreateEvaluateOffersRequest(Subject);
  if(request != null)
  {
    request.Issue(Subject);
  }  
}

