Player player = Subject;

// If this player is injured, decrement the injury duration.
if ((player.InjuryType != InjuryType.Invalid_) && (player.MaxInjuryDuration > 0) && (player.InjurySeverity != InjurySeverity.CareerEnding))
{
 player.MaxInjuryDuration = player.MaxInjuryDuration - 1;
 player.MinInjuryDuration = player.MinInjuryDuration - 1;

 // If the injury is healed, send an event and clear the injury.
 if (player.MaxInjuryDuration <= 0)
 {
  // Resetting injury status before sending the injury event.
  // Leaving severity and type reset until after event to avoid risk with this alpha change.
  bool wasEarlyReturn = player.InjuryStatus == InjuryStatus.EarlyReturn; // cache the status for the event.
  player.InjuryStatus = InjuryStatus.Uninjured;

  // Send the injury event.
  PlayerHealedEvent.Player = player;
  PlayerHealedEvent.Team = Team;
  PlayerHealedEvent.WasEarlyReturn = wasEarlyReturn;
  PlayerHealedEvent.Send();
  PlayerHealedEvent.Player = null;
  PlayerHealedEvent.Team = null;

  // Injury values (convert to integers for comparison).
  int playerInjurySeverity = player.InjurySeverity;
  int majorInjurySeverity = InjurySeverity.SeveralGames;
  int returningInjuryDuration = InjuryDuration.Returning;

  // If the player was out for several games, set his injury duration to "returning".
  // This tells the presentation system that the player's next game is his first game
  // back. The value will be reset to zero following the player's next game.
  if (playerInjurySeverity >= majorInjurySeverity)
  {
   player.MaxInjuryDuration = returningInjuryDuration;
   player.MinInjuryDuration = returningInjuryDuration;
  }
  else
  {
   // Clear the player injury information.
   player.InjurySeverity = InjurySeverity.Invalid_;
   player.InjuryType = InjuryType.Invalid_;
  }
 }
 else if (player.MinInjuryDuration == 0)    // If the injury min duration is reached, send an event
 {
  PlayerEarlyReturnEvent.Player = player;
  PlayerEarlyReturnEvent.Team = Team;
  PlayerEarlyReturnEvent.Send();
  PlayerEarlyReturnEvent.Player = null;
  PlayerEarlyReturnEvent.Team = null;
 }
}
else if ((player.InjuryType != InjuryType.Invalid_) && (player.InjurySeverity != InjurySeverity.CareerEnding))
{
 //Something is wrong with this player's injury information.
 if ((player.InjurySeverity == InjurySeverity.SeasonEnding) && (player.MaxInjuryDuration == 0))
 {
  player.MaxInjuryDuration = 30;
  player.MinInjuryDuration = 30;
  player.InjuryStatus = InjuryStatus.Injured;
 }
 else
 {
  player.MaxInjuryDuration = 0;
  player.MinInjuryDuration = 0;
  player.InjuryStatus = InjuryStatus.Uninjured;
  player.InjurySeverity = InjurySeverity.Invalid_;
  player.InjuryType = InjuryType.Invalid_;
 }
}

