        {
            package.Response = TradeResponse.DeclineBetterOffer;
        }
    }

    // If the package does not have a response, set it to "decline".
    if (package.Response == TradeResponse.None)
    {
        package.Response = TradeResponse.Decline;
    }

    // Issue the notification.
    TradeNegotiationManager.IssueTradeNegotiationNotification(teamB, Negotiation);
}

