int contractSalary = (baseContract.Salary - baseContract.Bonus) * contractLength;
int contractBonus  = baseContract.Bonus * contractLength;

// Create the draft player contract.
PlayerContract contract = CreatePlayerContract(contractSalary, contractBonus, contractLength);

contract;

