    player.ContractBonus3 = 0;
    player.ContractBonus4 = 0;
    player.ContractBonus5 = 0;
    player.ContractBonus6 = 0;
    player.ContractBonus7 = 0;
    player.ContractExtraYearOption = false;


    if (Franchise.HallOfFamePlayers.Contains(player))
    {
        Franchise.HallOfFamePlayers.FindAndErase(player);
    }
}

// Set the new contract status.
player.ContractStatus = newContractStatus;

// Clear the trade status whenever a player's contract status changes.
player.TradeStatus = TradeStatus.None;

// If the player changed teams, clear his consecutive years with team.
if ((oldTeam != newTeam) || (newTeam == null))
{
    player.PLYR_CONSECYEARSWITHTEAM = 0;
}

// If the team has changed teams and the old team is non-null, store it as the previous team.
if ((oldTeam != null) && (oldTeam != newTeam))
{
    player.PrevTeamIndex = oldTeam.TeamIndex;
}

// If the new team is valid, set the player's team index.
if (newTeam != null)
{
    player.TeamIndex = newTeam.TeamIndex;
}
// Otherwise, clear the team index.
else
{
    player.TeamIndex = TeamIndex.None;
}

Team playerPrevTeam = null;
if ( newTeam == null && oldTeam == null && player.PrevTeamIndex != TeamIndex.None )
{
     // Get Player's Previous Team Asset for Free Agents
     playerPrevTeam = TeamManager.FindTeamByTeamIndex(player.PrevTeamIndex);
}

// Construct the event.
Player_UpdatePlayerContractStatusEvent event = new Player_UpdatePlayerContractStatusEvent;
event.Player = player;
event.OldTeam = oldTeam;
event.NewTeam = newTeam;
event.PlayerPrevTeam = playerPrevTeam;
event.OldContractStatus = oldContractStatus;
event.NewContractStatus = newContractStatus;
event.WasFranchiseTagged = wasFranchiseTagged;
event.isDeadWeightCut = isDeadWeightCut;

// Log the event.
LogUpdatePlayerContractStatus(event);

// Send the event.
event.Send();

int popularity = 0;

int legacyMod = LegacySpline.CalculateY(player.LegacyScore);
int ageMod = PopAgeSpline.CalculateY(player.Age);
int personalityMod = OverallSpline.CalculateY(player.PersonalityRating);
PositionE position = player.Position;

popularity = player.GetBestOverallGrade();

popularity = (player.PersonalityRating * personalityMod) / 100;
popularity = (player.PersonalityRating * ageMod) / 100;
popularity = (player.PersonalityRating * legacyMod) / 100;

if (player.YearsPro == 0 && player.PLYR_DRAFTROUND == 1 && player.PLYR_DRAFTPICK <= 10)
{
     popularity = (popularity * 108) / 100;
}

if (position == PositionE.QB)
{
     popularity = (popularity * 100) / 100;
}
else if (position == PositionE.HB || position == PositionE.TE)
{
     popularity = (popularity * 95) / 100;
}
else if (position == PositionE.WR)
{
     popularity = (popularity * 95) / 100;
}
else if (position == PositionE.LE || position == PositionE.RE)
{ 
    popularity = (popularity * 85) / 100;
}
else if (position == PositionE.DT)
{
     popularity = (popularity * 75) / 100;
}
else if(position == PositionE.LOLB || position == PositionE.ROLB)
{
 popularity = (popularity * 85) / 100;
}
else if (position == PositionE.MLB)
{
    popularity = (popularity * 95) / 100;
}
else if (position == PositionE.CB)
{
    popularity = (popularity * 95) / 100;
}
else if (position == PositionE.FS)
{
    popularity = (popularity * 90) / 100;
}
else if (position == PositionE.SS)
{
    popularity = (popularity * 92) / 100;
}
else if (position == PositionE.C || position == PositionE.LT || position == PositionE.RG || position == PositionE.LG || position == PositionE.RT)
{
    popularity = (popularity * 65) / 100;
}
else if(position == PositionE.K || position == PositionE.P)
{
 popularity = (popularity * 10) / 100;
}
else
{
 popularity = (popularity * 10) / 100;
}

if (popularity > 99)
{
    popularity = 99;
}

popularity;

int cost = BaseCost;
if (player != null && team != null)
{
 if (player == team.GetBestPlayerAtPosition(player.Position))
    {
        cost = (cost * 125) / 100;
    }
 
 if (player.PlayerPercentage < playersPositionPercentageAverage)
 {
  cost = (cost * 90) / 100;
 }
 else
 {
  cost = (cost * 110) / 100;
 } 
}
cost;

float decreaseChance = 0.0;

if (score == -1)
{
  decreaseChance = 1.0;
}

decreaseChance;

float increaseChance = 0.0;

if (score == 1)
{
  increaseChance = 1.0;
}

increaseChance;

//Season score is currently implemented as a binary style evaluation. There are 3 possible values for season Score

int Increase = 1; //Set season score to Increase to increase player's DevTrait by 1
int Decrease = -1; //Set season score to Decrease to decrease player's DevTrait by 1
int NoChange = 0; //Default value will neither Increase or Decrease player's DevTrait

int seasonScore = NoChange;
PositionE position = player.Position;
int GamesStarted = 0;

TraitDevelopment playerDevTrait = player.TraitDevelopment;

if (player.SeasonStats != null && player.SeasonStats.Population() - 1 >= 0)
{
 GamesStarted = player.SeasonStats[player.SeasonStats.Population() - 1].GAMESSTARTED;
}

if (position == PositionE.QB)
{
  if (playerDevTrait  == TraitDevelopment.SuperStar)
  {
    int PassTDRank = StatManager.FindPlayerRankingByStat(player, PositionE.QB, StatType.PassTds);
    int PassYDSRank = StatManager.FindPlayerRankingByStat(player, PositionE.QB, StatType.PassYards);
    int AverageRank = (((PassTDRank * 10) + (PassYDSRank * 10))/2)/10;
    if (AverageRank > 6)
    {
      seasonScore = Decrease; //Decrease if you aren't maintaining high performance
    }
  }
  else if (playerDevTrait  == TraitDevelopment.Star)
  {
    int PassTDRank = StatManager.FindPlayerRankingByStat(player, PositionE.QB, StatType.PassTds);
    int PassYDSRank = StatManager.FindPlayerRankingByStat(player, PositionE.QB, StatType.PassYards);
    int AverageRank = (((PassTDRank * 10) + (PassYDSRank * 10))/2)/10;
    if (AverageRank <= 6)
      {
     seasonScore = Increase;
  }
    else if (AverageRank > 8)
  {
    seasonScore = Decrease;
  }
  }
  else if (playerDevTrait == TraitDevelopment.Quick)
  {
  if (GamesStarted <= 5 && player.YearsPro >= 2)
  {
    seasonScore = Decrease;
  }
  else if (GamesStarted >= 12)
  {
    int PassTDRank = StatManager.FindPlayerRankingByStat(player, PositionE.QB, StatType.PassTds);
    int PassYDSRank = StatManager.FindPlayerRankingByStat(player, PositionE.QB, StatType.PassYards);
    int AverageRank = (((PassTDRank * 10) + (PassYDSRank * 10))/2)/10;

    if (AverageRank <= 8)
    {
      seasonScore = Increase;
    }
    else if (AverageRank >= 18)
    {
      seasonScore = Decrease;
    }
    }
  }
  else if (playerDevTrait == TraitDevelopment.Normal)
  {
  if (GamesStarted >= 12)
  {
    int PassTDRank = StatManager.FindPlayerRankingByStat(player, PositionE.QB, StatType.PassTds);
    int PassYDSRank = StatManager.FindPlayerRankingByStat(player, PositionE.QB, StatType.PassYards);
    int AverageRank = (((PassTDRank * 10) + (PassYDSRank * 10))/2)/10;

    if (AverageRank <= 18)
    {
      seasonScore = Increase;
    }
  }
  }
}
else if (position == PositionE.HB)
{
   if (playerDevTrait == TraitDevelopment.SuperStar)
   {
      int RushTDRank = StatManager.FindPlayerRankingByStat(player, PositionE.HB, StatType.RushTds);
      int RushYDSRank = StatManager.FindPlayerRankingByStat(player, PositionE.HB, StatType.RushYards);
      int AverageRank = (((RushTDRank * 10) + (RushYDSRank * 10))/2)/10;
  
