  int SacksRank = StatManager.FindPlayerRankingByStat(player, PositionE.LOLB, StatType.DefensiveSacks);
         int TFLrank= StatManager.FindPlayerRankingByStat(player, PositionE.LOLB, StatType.DefensiveTacklesForLoss);
         int TacklesRank = StatManager.FindPlayerRankingByStat(player, PositionE.LOLB, StatType.DefensiveTackles);
    int AverageRank = (((SacksRank * 10) + (TFLrank * 10) + (TacklesRank * 10))/3)/10;

         if (AverageRank <= 12)
         {
            seasonScore = Increase;
         }
      }
  }
}
else if (position == PositionE.ROLB)
{
   if (playerDevTrait == TraitDevelopment.SuperStar)
   {
      int SacksRank = StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveSacks);
         int TFLrank= StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveTacklesForLoss);
         int TacklesRank = StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveTackles);
    int AverageRank = (((SacksRank * 10) + (TFLrank * 10) + (TacklesRank * 10))/3)/10;
  
      if (AverageRank > 6)
      {
         seasonScore = Decrease;
      }
   }  
   else if (playerDevTrait == TraitDevelopment.Star)
   {
  int SacksRank = StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveSacks);
         int TFLrank= StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveTacklesForLoss);
         int TacklesRank = StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveTackles);
    int AverageRank = (((SacksRank * 10) + (TFLrank * 10) + (TacklesRank * 10))/3)/10;
  
      if (AverageRank <= 6)
      {
         seasonScore = Increase;
      }
      else if (AverageRank > 8)
      {
         seasonScore = Decrease;
      }
   }
   else if (playerDevTrait == TraitDevelopment.Quick)
   {
      if (GamesStarted <= 3 && player.YearsPro >= 2)
      {
         seasonScore = Decrease;
      }
      else if (GamesStarted >= 12)
      {
  int SacksRank = StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveSacks);
         int TFLrank= StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveTacklesForLoss);
         int TacklesRank = StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveTackles);
    int AverageRank = (((SacksRank * 10) + (TFLrank * 10) + (TacklesRank * 10))/3)/10;

         if (AverageRank <= 8)
         {
            seasonScore = Increase;
         }
         else if (AverageRank >= 16)
         {
            seasonScore = Decrease;
         }
      }
   }
   else if (playerDevTrait == TraitDevelopment.Normal)
   {
      if (GamesStarted >= 12)
      {
  int SacksRank = StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveSacks);
         int TFLrank= StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveTacklesForLoss);
         int TacklesRank = StatManager.FindPlayerRankingByStat(player, PositionE.ROLB, StatType.DefensiveTackles);
    int AverageRank = (((SacksRank * 10) + (TFLrank * 10) + (TacklesRank * 10))/3)/10;

