//Make sure Normal can't decrease and Superstar can't increase
if (playerDevTrait == TraitDevelopment.Normal && seasonScore == Decrease)
{
  seasonScore = NoChange;
}
else if (playerDevTrait == TraitDevelopment.SuperStar && seasonScore == Increase)
{
  seasonScore = NoChange;
}

