xpThreshold;

int modifiedPoints = pointsToModify;
TraitDevelopment playerDevTrait = player.TraitDevelopment;  

  //Following logic modifies regression points based on Dev Trait

  if(playerDevTrait == TraitDevelopment.Normal || playerDevTrait == TraitDevelopment.Quick)
  {
    modifiedPoints = modifiedPoints;
  }
  else if(playerDevTrait  == TraitDevelopment.Star)
  {
    modifiedPoints = modifiedPoints - 1;
  if (player.Age <= 29)
  {
    modifiedPoints = 0;
  }
  }
  else if(playerDevTrait == TraitDevelopment.SuperStar)
  {
    modifiedPoints = modifiedPoints - 2;
  if (player.Age <= 30)
  {
    modifiedPoints = 0;
  }
  }

//Clamp to 0
if (modifiedPoints < 0)
{
  modifiedPoints = 0;
}



modifiedPoints;

