int spline2        = PercentageAppt2Spline.CalculateY(playerPercentage);
int spline3        = PercentageAppt3Spline.CalculateY(playerPercentage);
int spline4        = PercentageAppt4Spline.CalculateY(playerPercentage);
int spline5        = PercentageAppt5Spline.CalculateY(playerPercentage);

chances.Appointment1 = spline1;
