chances.Appointment4 = spline4;
chances.Appointment5 = spline5;

int retireChance = 0;
int playerPercentage = player.PlayerPercentage;
int percentageMod = YearsProModSpline.CalculateY(playerPercentage);
int ageMod = AgeModSpline.CalculateY(player.Age);
int injMod = DurabilityModifierSpline.CalculateY(player.InjuryRating);

retireChance = (percentageMod + ageMod + injMod);

int coachMod = GetModifiedRetirementChance(player, team);
retireChance = retireChance - coachMod;

if ((player.Position != PositionE.QB && player.Position != PositionE.K) && player.Age >= 40)
 {
      retireChance = 100;
 }
else if (player.Age >= 43)
{
  retireChance = 100;
}

if (player.Position == PositionE.QB)
{
 //QB Related stuff
  if (player.Age > 34 && player.PlayerType == PlayerType.QB_Scrambler && player.PlayerPercentage > 20)
  {
    retireChance=100;
  }
  if (player.Age >= 36  && player.PlayerPercentage >= 50)
  {
    retireChance = 100;
  }
  //Special Casing to ensure Tom Brady does not retire in year 1
  if (player.PresentationId == 1327 && player.PLYR_BIRTHDATE == 7077 && player.Age < 42)
  {
    retireChance = 0;
  }
}

else if (player.Position == PositionE.HB)
 {
 //HB related stuff
   if (player.Age >= 32 && player.Age < 35 && player.PlayerPercentage < 5 )
 {
  retireChance = 0;
 }

   if (player.Age >= 35)
   {
        retireChance = 100;
   }
 }

else if (player.Position == PositionE.K || player.Position == PositionE.P)
{
 //Check to make sure good kickers/punters do not retire early
 if (Player.Age < 38 && player.PlayerPercentage < 20)
  {
   retireChance = 0;
  }
}

if (player.Age < 32 && player.Position != PositionE.HB)
 {
      retireChance = 0;
 }


if (player.Age < 30)
 {
      retireChance = 0;
 }

if (player.InjurySeverity == InjurySeverity.CareerEnding)
 {
      retireChance = 100;
 }

retireChance;

int returnVal = 0;

int progress = GetProgress(player, team, playerSeasonStats, teamSeasonStats, evalInfo);

if (progress >= CompletionValue[4])
{
    returnVal = 4;
}
else if (progress >= CompletionValue[3])
{
    returnVal = 3;
}
else if (progress >= CompletionValue[2])
{
    returnVal = 2;
}
else if (progress >= CompletionValue[1])
{
    returnVal = 1;
}

returnVal;

int progress = GetProgress(player, team, playerSeasonStats, teamSeasonStats, evalInfo );
int progressPercentage = (100 * progress)/ CompletionValue[4];

if (progressPercentage > 100) 
{
    progressPercentage = 100;
}

progressPercentage;

