    request.IsResponseRequired = false;
    request.IsConnectionRequired = false;
    request.IsBlocking = false;

    request.PlayerSigningEvalRef = this;
    request.OfferList = playerOfferList;
    request.player = player;
    request.RequestData = playerOfferList;

