// Only on-team players or free agents are eligible to sign with a practice squad.
bool isEligible = (player.PracticeSquadYears < RosterInfo.PlayerMaxPracticeSquadYears) && player.YearsPro < 2
    && ((player.ContractStatus >= PlayerContractStatus.FirstOnTeam_ && player.ContractStatus <= PlayerContractStatus.LastOnTeam_ && player.ContractStatus != PlayerContractStatus.PracticeSquad && player.PLYR_DRAFTROUND > 3 )
        || player.ContractStatus == PlayerContractStatus.FreeAgent);
    
isEligible;

// User-controlled players should not sign with a practice squad.
bool isInterested = (player.PlayerPercentage > MinPercentageForPracticeSquad)
    && (player.Age < MaxAgeForPracticeSquad)
    && (!player.IsUserControlled);

isInterested;

PlayerReSignNegotiation[] negotiations = GetPlayerReSignListForTeam(team);

// Create the user-facing re-sign request.
if (negotiations.Population() > 0)
