SignedPracticeSquadPlayerRequest request = new SignedPracticeSquadPlayerRequest;

// Set the request settings.
request.IsResponseRequired = false;
request.IsConnectionRequired = false;
request.IsBlocking = false;

// Set the request data.
request.Player = player;
request.OldTeam = oldTeam;
request.NewTeam = newTeam;
request.PlayerSigningEval = this;
request.RequestData = newTeam;

// Set the response form.
