if (IsDelegateResponseAvailable)
{
    request.ResponseForms.Append(SignedPracticeSquadPlayer_DelegateResponse);
}

// Set the default response.
request.DefaultResponse = SignedPracticeSquadPlayer_DelegateResponse;

// Issue the request to the team which signed the player.
TeamRequestManager.Issue(newTeam, request);

ContractOffer selectedOffer = null;
Player player = request.player;
ContractOffer[] offerList = null;
Team noTeam = null;


// If the response is "Select Offer", simply store the selected item.
if(request.Response == request.SelectOfferResponse)
{
    selectedOffer = null;
}

// If an offer was selected, sign the player to the new contract.
if(selectedOffer != null)
{
    PlayerManagerRef.SignPlayer(selectedOffer.Team, player, selectedOffer.Contract);
    LogPlayerDecision(player, selectedOffer.Team,  PlayerDecision.Accept);
}
else
{
    LogPlayerDecision(player, noTeam,  PlayerDecision.Wait);
}

// Remove all outstanding offers from the global list.
if((selectedOffer != null) || (FranchiseRef.SeasonInfo.CurrentFreeAgencyStage >= FranchiseRef.SeasonInfo.TotalFreeAgencyStages))
{
    if((OutstandingOffers != null) && (offerList != null))
    {
        if((OutstandingOffers.Population() > 0) && (offerList.Population() > 0))
        {
            RemoveContractOffers removeOffersView = new RemoveContractOffers;
            removeOffersView.OutstandingOffers = OutstandingOffers;
            removeOffersView.Subjects = offerList;
            removeOffersView.Update();
        }
    }
}

//Run CPU logic
if(request.Response == request.DefaultResponse)
{
    Team team = request.team;
    Player player = SelectPlayerForFranchiseTag(team);

    if(player != null)
    {
        PlayerContract contract = GenerateFranchiseTagContract(player);
        PlayerManagerRef.ReSignPlayer(team, player, contract, true);

        PlayerReSignNegotiation resignNegotiation = GetTeamNegotiationWithPlayer(team, player);

        ReSignNegotiationManager.EndReSignTransaction(resignNegotiation);

        PlayerReSignList.FindAndErase(resignNegotiation);
    }
}

// If the "Delegate" response was chosen, update offers for the selected team.
if (request.Response == request.DelegateResponse && FranchiseRef.SeasonInfo.IsFreeAgentPeriodActive)
{
    Team team = request.Team;
    
    // FA evaluation already done in Make Team Offers Transaction IsCPUSignOffseasonFAEnabled is true
    if(team.TeamSettingRef.IsCPUSignOffseasonFAEnabled == false)
    {
        // Retrieve the current free agency stage as a zero-based number.
        int currentStage = FranchiseRef.SeasonInfo.CurrentFreeAgencyStage / 2;
    
        // Run free agent evaluation logic for the requested team.
        BeginTeamFreeAgentEvaluation(currentStage, FranchiseRef.FreeAgents, OutstandingOffers);
        {
            EvaluateTeamFreeAgentOffers(team);
        }
        EndTeamFreeAgentEvaluation();
    }
}

