int teamInterest = 0;
if (player != null)
{
   int playerPercentage = player.PlayerPercentage;
   PositionE position = player.Position;
   DepthChart depthChart = team.DepthChart;
   PlayerAcquisitionEvaluation playerEvaluation = team.Roadmap.FindBestPlayerEvaluation(player);

   if (playerEvaluation != null && depthChart != null && position != PositionE.Invalid_)
   {
      if (position == PositionE.QB)
      {
         if ( (player.Age <= 32 && playerPercentage <= 25) || (player.Age < 34 && playerPercentage <= 10) || (player.Age < 38 && playerPercentage <= 5))
         {
            teamInterest = 100;
         }
         
         if (player.SuperBowlWins >= 2)
         {
            teamInterest = 100;
         }
      }
      else if (position == PositionE.HB)
      {
         Player starter = depthChart.GetPlayer(position, 0);
         if (starter != null)
         {
            if (player == starter)
            {
               if ((player.YearsPro >= 3 && playerPercentage <= 25 ) || (player.Age < 29 && playerPercentage <= 15) || (player.TraitDevelopment == TraitDevelopment.SuperStar))
               {
                  teamInterest = 100;
               }
            }

       else if (player.YearsPro <= 3)
            {
               teamInterest = 100;
            }

            if (player.TraitDevelopment == TraitDevelopment.Normal)
            {
               teamInterest = 0;
            }
         }
      }
      else if (position == PositionE.FB || position == PositionE.K || position == PositionE.P)
      {
         if ((player.Age <= 32 && playerPercentage <= 30) || (player.Age > 32 && playerPercentage <= 20))
         {
            teamInterest = 100;
         }
      }
      else if (position == PositionE.WR)
      {
         if ((player.Age <= 28 && playerPercentage <= 16) || (player.Age <= 31 && playerPercentage <= 14) || (player.Age < 34 && playerPercentage <= 5 ) || (player.TraitDevelopment == TraitDevelopment.SuperStar))
         {
            teamInterest = 100;
         }
      }
      else if (position == PositionE.TE)
      {
         if ((player.Age <= 26 && playerPercentage <= 25) || (player.Age <= 28 && playerPercentage <= 20) || (player.Age < 34 && playerPercentage <= 10))
         {
            teamInterest = 100;
         }
      }
      else if (position == PositionE.DT || position == PositionE.LE || position == PositionE.RE)
  {
    Player starter = depthChart.GetPlayer(position, 0);
         if (starter != null)
    {
    if (player == starter)
      {
      if ((player.Age <= 28 && playerPercentage <= 30) ||  (player.Age <= 30 && playerPercentage <= 20) || (player.Age < 34 && playerPercentage <= 10) || (player.Age >= 34 && playerPercentage <= 8) || (player.TraitDevelopment == TraitDevelopment.SuperStar) )  
        {
                  teamInterest = 100;
               }
      }
    else if (player != starter)
      {
      if ( (playerPercentage <= 25) || (player.TraitDevelopment == TraitDevelopment.Star) || (player.TraitDevelopment == TraitDevelopment.SuperStar) )  
        {
                  teamInterest = 100;
               }
      }
    }
    
  }

      else if ((position >= PositionE.FirstOffenseLine_ && position <= PositionE.LastOffenseLine_) || (position >= PositionE.FirstDefenseLB_ && position <= PositionE.LastDefenseLB_))
      {
         if ((player.Age <= 28 && playerPercentage <= 30) ||  (player.Age <= 30 && playerPercentage <= 20) || (player.Age < 34 && playerPercentage <= 10) || (player.Age >= 34 && playerPercentage <= 8) || (player.TraitDevelopment == TraitDevelopment.SuperStar) )
         {
            teamInterest = 100;
         }
      }
      else if (position >= PositionE.FirstDefenseDB_ && position <= PositionE.LastDefenseDB_)
      {
         if ((player.Age <= 28 && playerPercentage <= 30) || (player.Age < 34 && playerPercentage <= 20) || (player.TraitDevelopment == TraitDevelopment.SuperStar))
         {
            teamInterest = 100;
         }
      }
   }
}
teamInterest;

int value = GetCompletionValue(player, team, opposingTeam, evalInfo);

if (value < MinCompletionValue)
{
    value = MinCompletionValue;
}

if (value > MaxCompletionValue)
{ 
    value = MaxCompletionValue;
}

value;

int value = GetConfidenceAward(player, team, evalInfo, seasonGame, opposingTeam);

if (value < MinConfidenceAward)
{
    value = MinConfidenceAward;
}

if (value > MaxConfidenceAward)
{ 
    value = MaxConfidenceAward;
}

value;

