if (value < MinExperienceAward)
{
    value = MinExperienceAward;
}

if (value > MaxExperienceAward)
{ 
    value = MaxExperienceAward;
}

value;

int confidence = BaseConfidenceAward;

if (player != null && evalInfo != null)
{
 int percentageMod = PercentageSpline.CalculateY(player.PlayerPercentage);
    int ageMod = AgeSpline.CalculateY(player.Age);
    PositionE position = player.Position;

    confidence = (confidence * percentageMod) / 100;
    confidence = (confidence * ageMod) / 100;

    if (player.TraitDevelopment == TraitDevelopment.SuperStar)
    {
        confidence = (confidence * 175) / 100;
    }

    if (player.TraitDevelopment == TraitDevelopment.Star)
    {
        confidence = (confidence * 125) / 100;
    }

   if (player.TraitDevelopment == TraitDevelopment.Normal)
    {
        confidence = (confidence * 75) / 100;
    }
   
    if (player.PersonalityRating > 75)
    { 
       confidence = (confidence * 110) / 100;
    }
   
    if (evalInfo.IsPlayerStarter)
    {
        if (player.YearsPro < 5)
        {
           if (player.PLYR_DRAFTROUND == 1)
           {
               confidence = confidence;
           }
         
           if (player.Background == PlayerBackstory.Undrafted)
           {
               confidence = (confidence * 125) / 100;
           }
         
           if(player.Background == PlayerBackstory.LateDraftPick)
           {
               confidence = (confidence * 115) / 100;
           }
        }
        else
        {
           if (player.InjuryRating > 75)
           {
               confidence = (confidence * 115) / 100;
           }
         
           if (player.SuperBowlWins > 0)
           {
               confidence = (confidence * 120) / 100;
           }
         
           if (player.ProBowlAppearences > 1)
           {
               confidence = (confidence * 110) / 100;
           }
        }
    }
    else if (PrimaryTeamStat != TeamStatType.Invalid)
    {
   if ((position >= PositionE.FirstOffenseLine_ && position <= PositionE.LastOffenseLine_) || (position >= PositionE.FirstDefenseLine_ && position <= PositionE.LastDefenseLine_))
   {
         confidence = 0;
   }
 else
 {
  confidence = MinConfidenceAward;
 }
      }

 if (player.ConfidenceRating > 85)
 {
 confidence = MinConfidenceAward;
 if (MaxConfidenceAward < 0)
 {
  confidence = MaxConfidenceAward;
 }
  
 }
 else if (player.ConfidenceRating < 30)
 {
 confidence = (confidence * 250) / 100;
 if (MaxConfidenceAward < 0)
 {
  confidence = MinConfidenceAward;
 }
 }
   
    if (player.IsUserControlled)
    {
        confidence = (confidence * 75) / 100;
    }
}
confidence;

//Returns ExperienceAward as Int
int award = BaseExperienceAward;
int percentageMod = PercentageSpline.CalculateY(player.PlayerPercentage);
int ageMod = AgeSpline.CalculateY(player.Age);

PositionE position = player.Position;

int devTrait = 0;

if (player.TraitDevelopment == TraitDevelopment.SuperStar)
{
    devTrait = 3;
}
else if (player.TraitDevelopment == TraitDevelopment.Star)
{
    devTrait = 2;
}
else if (player.TraitDevelopment == TraitDevelopment.Normal)
{
    devTrait = 0;
}
else 
{
 devTrait = 1;
}

int developmentMod = DevelopmentSpline.CalculateY(devTrait);

award = (award * percentageMod ) / 100;

award = (award * ageMod) / 100;

award = (award * developmentMod) / 100;

// CoachTrait level impact is applied in C code.  Use PlayerGoalsEval.CoachTraitInfluenceSpline to tune it.

if ((position >= PositionE.FirstOffenseLine_ && position <= PositionE.LastOffenseLine_) || (position >= PositionE.FirstDefenseLine_ && position <= PositionE.LastDefenseLine_))
{
 if (evalInfo.IsPlayerStarter)
 {
  award = award;
 }
 else
 {
  award = (award * 50) / 100;
 }
}

if (!evalInfo.IsPlayerStarter && PrimaryTeamStat != TeamStatType.Invalid)
{
 award = 0;
}

if (player.IsUserControlled == true)
{
    award = (award * 50) / 100;
}

award;

