  int xpAward = RandomRef.GetRandomIntRange(MinFocusTrainingXpAward, MaxFocusTrainingXpAward);
  player.ExperiencePoints = player.ExperiencePoints + xpAward;

  PersonHistoryTrackerNotificationEvent event = PlayerHistoryEventRef;
