// Get the season info needed
SeasonWeekType weekType = SeasonInfo.CurrentWeekType;
int week = SeasonInfo.CurrentWeek;

if (inSeasonRequest)
{
    // Get the info for the next game
    SeasonGame seasonGame = SeasonManager.GetNextGame(team, weekType, week);

    bool hadPlayoffByeWeek = false;

    // Handle Regular Season Bye Weeks
    if (seasonGame == null)
    {
        // Likely a bye week, GetNextGame gets the game for a specific week type and week
        // Pull in the next week, to use next relevant playbooks in practice

        if(weekType == SeasonWeekType.RegularSeason)
        {
            seasonGame = SeasonManager.GetNextGame(team, weekType, week + 1);
        }
        else
        {
            SeasonWeekType nextWeekType = SeasonWeekType.OffSeason;
            if (weekType == SeasonWeekType.WildcardPlayoff)
            {
                nextWeekType = SeasonWeekType.DivisionalPlayoff;
            }
            else if (weekType == SeasonWeekType.DivisionalPlayoff)
            {
                nextWeekType = SeasonWeekType.ConferencePlayoff;
            }
            else if (weekType == SeasonWeekType.ConferencePlayoff)
            {
                nextWeekType = SeasonWeekType.SuperBowl;
            }

            if(nextWeekType != SeasonWeekType.OffSeason)
            {
                seasonGame = SeasonManager.GetNextGame(team, nextWeekType, week + 1);
            }
        }

        if(weekType == SeasonWeekType.WildcardPlayoff)
        {
            hadPlayoffByeWeek = true;
        }
    }


    // If a game was found
    if (seasonGame != null)
    {
        // Create a practice game
        SeasonGame practiceGame = new SeasonGame;
        practiceGame.TimeOfDay = 975;
        practiceGame.SeasonYear = seasonGame.SeasonYear;
        practiceGame.SeasonWeekType = seasonGame.SeasonWeekType;
        practiceGame.SeasonGameNum = 0;
        practiceGame.GameStatus = GameStatus.Unplayed;
        practiceGame.IsPractice = true;
        practiceGame.ThanksgivingFlag = false;

        // Make sure the team is the home team, this is used so the practice session receives relevant playbooks
        if (team == seasonGame.HomeTeam)
        {
            practiceGame.HomeTeam = seasonGame.HomeTeam;
            if(!hadPlayoffByeWeek)
            {
                practiceGame.AwayTeam = seasonGame.AwayTeam;
            }
            else
            {
                practiceGame.AwayTeam = PracticeTeam;
            }
        }
        else
        {
            practiceGame.HomeTeam = seasonGame.AwayTeam;
            if(!hadPlayoffByeWeek)
            {
                practiceGame.AwayTeam = seasonGame.HomeTeam;
            }
            else
            {
                practiceGame.AwayTeam = PracticeTeam;
            }
        }

        // Create the game request
        PracticeRequest gameRequest = CreatePracticeRequest(team);
        gameRequest.RequestData = practiceGame;
        gameRequest.LeagueOwnerTutorialStage = TutorialManager.LeagueOwnerWalkthroughStage;

  if(team.UserCharacter != null && isUserPlayer)
  {
      gameRequest .ResponseForms = new ResponseForm[2];
      gameRequest .ResponseForms.Append(PracticePlayerResponse);
      gameRequest .ResponseForms.Append(PracticeRequestDelegateResponse);
      gameRequest .DefaultResponse = PracticePlayerResponse;
      gameRequest .IsPersistent = false;
  }

        // Set team on request
        gameRequest.Team = team;
        gameRequest.UserEntity = team.UserCharacter;

        // Issue the request
        gameRequest.Issue(team.UserCharacter);

        practiceGame.HomeRequestId = gameRequest.RequestId;

    }
}

