PlayerSigningEvalRef.ProcessFranchiseTagRequest(this);

if( team.HeadCoach != null )
{
 if( !team.HeadCoach.IsUserControlled || 
   ( team.HeadCoach.IsUserControlled  && ( (Response == DefaultResponse) || (Response == null)  && !team.TeamSettingRef.IsCPUReSignPlayersEnabled ) ) ) 
 {
  PlayerReSignNegotiation[] ReSignResults = PlayerSigningEvalRef.GetPlayerReSignListForTeam(team);
  if(ReSignResults.Population() >= 1)
  {
   ReSignNegotiationManager.IssuePlayerRequests(ReSignResults);
  }
 }
}

//else ifs for the combinations


if (reSignNegotiation.ActualDesiredContractSalary > reSignNegotiation.ContractSalary && reSignNegotiation.ActualDesiredContractBonus > reSignNegotiation.ContractBonus && reSignNegotiation.ActualDesiredContractLength <= reSignNegotiation.ContractLength)
  {
    
    reSignNegotiation.ReSignStatus = ReSignStatus.GoodLengthResponse;
  }

else if (reSignNegotiation.ActualDesiredContractSalary > reSignNegotiation.ContractSalary && reSignNegotiation.ActualDesiredContractBonus > reSignNegotiation.ContractBonus && reSignNegotiation.ActualDesiredContractLength > reSignNegotiation.ContractLength)
  {
    
    reSignNegotiation.ReSignStatus = ReSignStatus.AllBadResponse;

  }
else if (reSignNegotiation.ActualDesiredContractSalary > reSignNegotiation.ContractSalary && reSignNegotiation.ActualDesiredContractBonus <= reSignNegotiation.ContractBonus && reSignNegotiation.ActualDesiredContractLength <= reSignNegotiation.ContractLength)
  {
  
    reSignNegotiation.ReSignStatus = ReSignStatus.GoodBonusLengthResponse;
  }
else if (reSignNegotiation.ActualDesiredContractSalary > reSignNegotiation.ContractSalary && reSignNegotiation.ActualDesiredContractBonus <= reSignNegotiation.ContractBonus && reSignNegotiation.ActualDesiredContractLength > reSignNegotiation.ContractLength)
  {
    
    reSignNegotiation.ReSignStatus = ReSignStatus.GoodBonusResponse;
  }
else if (reSignNegotiation.ActualDesiredContractSalary <= reSignNegotiation.ContractSalary && reSignNegotiation.ActualDesiredContractBonus > reSignNegotiation.ContractBonus && reSignNegotiation.ActualDesiredContractLength <= reSignNegotiation.ContractLength)
  {
   
    reSignNegotiation.ReSignStatus = ReSignStatus.GoodSalaryLengthResponse;
  }
else if (reSignNegotiation.ActualDesiredContractSalary <= reSignNegotiation.ContractSalary && reSignNegotiation.ActualDesiredContractBonus <= reSignNegotiation.ContractBonus && reSignNegotiation.ActualDesiredContractLength > reSignNegotiation.ContractLength)
  {
  
    reSignNegotiation.ReSignStatus = ReSignStatus.GoodSalaryBonusResponse;
  }  
else if (reSignNegotiation.ActualDesiredContractSalary <= reSignNegotiation.ContractSalary && reSignNegotiation.ActualDesiredContractBonus > reSignNegotiation.ContractBonus && reSignNegotiation.ActualDesiredContractLength > reSignNegotiation.ContractLength)
  {
   
    reSignNegotiation.ReSignStatus = ReSignStatus.GoodSalaryResponse;
  }

if(reSignNegotiation.PlayerReSignInterest >= 80)
{
    AcceptOffer(reSignNegotiation);
}
else
    {
        DeclineNegotiations(reSignNegotiation);
    }

reSignNegotiation.ReSignStatus = ReSignStatus.NotInterested;

PlayerSigningEval.LogPlayerDecision(reSignNegotiation.Player, reSignNegotiation.Team,  PlayerDecision.Decline);
EndReSignTransaction(reSignNegotiation);

//Send negotiation breakdown event
BreakdownEvent.contractNegotiation = reSignNegotiation;
BreakdownEvent.Send();
BreakdownEvent.contractNegotiation = null;

// Determine if player should accept, decline, or wait on the offer.
bool isWaitingOnOffer = false;

reSignNegotiation.NegotiationCount = reSignNegotiation.NegotiationCount + 1;
int randomPct = Random.GetRandomIntRange( 0, 100 );

//if at any point they offer more than the actual desired, the player should accept the offer. 
if (reSignNegotiation.NegotiationCount >= 1 && (reSignNegotiation.ContractSalary >= reSignNegotiation.ActualDesiredContractSalary && reSignNegotiation.ContractBonus >= reSignNegotiation.ActualDesiredContractBonus && reSignNegotiation.ContractLength >= reSignNegotiation.ActualDesiredContractLength) )
  {
    AcceptOffer(reSignNegotiation);
  }
//accept any offer that's at least 125% of the players actual desired contract value when offering 1 year less than desired
else if (reSignNegotiation.NegotiationCount >= 1 && (reSignNegotiation.ContractSalary >= ((reSignNegotiation.ActualDesiredContractSalary*5)/4) && reSignNegotiation.ContractBonus >= ((reSignNegotiation.ActualDesiredContractBonus*5)/4) && reSignNegotiation.ContractLength == reSignNegotiation.ActualDesiredContractLength - 1))
  {
    AcceptOffer(reSignNegotiation);
  }
//accept any offer that's at least 150% of value no matter the contract length
else if (reSignNegotiation.NegotiationCount >= 1 && (reSignNegotiation.ContractSalary >= ((reSignNegotiation.ActualDesiredContractSalary*3)/2) && reSignNegotiation.ContractBonus >= ((reSignNegotiation.ActualDesiredContractBonus*3)/2)))
  {
    AcceptOffer(reSignNegotiation);
  }

//If this is the off-season, make a decision now.
else if(SeasonInfo.CurrentStage == SeasonStage.OffSeason && (reSignNegotiation.ContractSalary < reSignNegotiation.ActualDesiredContractSalary || reSignNegotiation.ContractBonus < reSignNegotiation.ActualDesiredContractBonus || reSignNegotiation.ContractLength < reSignNegotiation.ActualDesiredContractLength))
  {
    //instant rejection
    DeclineNegotiations(reSignNegotiation);
  }
//Never reject on first offer 1st offer
else if(reSignNegotiation.NegotiationCount == 1 && (reSignNegotiation.ContractSalary < reSignNegotiation.ActualDesiredContractSalary || reSignNegotiation.ContractBonus < reSignNegotiation.ActualDesiredContractBonus || reSignNegotiation.ContractLength < reSignNegotiation.ActualDesiredContractLength))
  {
        //we need something to wait for next week
        isWaitingOnOffer = true;
  }
//2nd offer rejection chance
else if (reSignNegotiation.NegotiationCount == 2 && (reSignNegotiation.ContractSalary < reSignNegotiation.ActualDesiredContractSalary || reSignNegotiation.ContractBonus < reSignNegotiation.ActualDesiredContractBonus || reSignNegotiation.ContractLength < reSignNegotiation.ActualDesiredContractLength))
  {
    //increase rejection chance
    if (randomPct < 80)
      {
        //we need something to wait for next week
        isWaitingOnOffer = true;
      }
    else 
      {
        //instant rejection
        DeclineNegotiations(reSignNegotiation);
      }
  }
//3rd offer rejection chance
else if (reSignNegotiation.NegotiationCount == 3 && (reSignNegotiation.ContractSalary < reSignNegotiation.ActualDesiredContractSalary || reSignNegotiation.ContractBonus < reSignNegotiation.ActualDesiredContractBonus || reSignNegotiation.ContractLength < reSignNegotiation.ActualDesiredContractLength))
  {
    //increase rejection chance
    if (randomPct < 70)
      {
        //we need something to wait for next week
        isWaitingOnOffer = true;
      }
    else 
      {
        //instant rejection
        DeclineNegotiations(reSignNegotiation);
      }
  }
//4th offer rejection chance
else if (reSignNegotiation.NegotiationCount >= 4 && (reSignNegotiation.ContractSalary < reSignNegotiation.ActualDesiredContractSalary || reSignNegotiation.ContractBonus < reSignNegotiation.ActualDesiredContractBonus || reSignNegotiation.ContractLength < reSignNegotiation.ActualDesiredContractLength))
  {
    //50 percent chance of rejection
    if (randomPct < 50)
      {
        //we need something to wait for next week
        isWaitingOnOffer = true;
      }
    else 
      {
        //instant rejection
        DeclineNegotiations(reSignNegotiation);
      }
  }

// Determine if player signed or declined the contract.
bool contractAccepted = (reSignNegotiation.ReSignStatus == ReSignStatus.ReSigned);

//If we're waiting on an offer, interest status should change based on how good/poor the new offer is
if(isWaitingOnOffer)
{
    AdjustPlayerReSignStatus(reSignNegotiation);
    
    // Wait on offer after the status/values have been updated
    WaitOnOffer(reSignNegotiation);
}
// Send out notifications for the negotiation's conclusion
else if(contractAccepted)
{        
    // Erase negotiation for signed players.
    PlayerSigningEval.PlayerReSignList.FindAndErase(reSignNegotiation);
}

reSignNegotiation.PreviousOfferedContractSalary = reSignNegotiation.ContractSalary;
reSignNegotiation.PreviousOfferedContractBonus = reSignNegotiation.ContractBonus;
reSignNegotiation.PreviousOfferedContractLength = reSignNegotiation.ContractLength;


isWaitingOnOffer;

// Return a ContractOffer record from a PlayerReSignNegotiation record.
PlayerContract contract = PlayerContractManager.CreatePlayerContract(reSignNegotiation.ContractSalary, reSignNegotiation.ContractBonus, reSignNegotiation.ContractLength);

// we need to turn the player contract into a contract offer
ContractOffer contractOffer = new ContractOffer;
contractOffer.Player = reSignNegotiation.Player;
contractOffer.Team = reSignNegotiation.Team;
contractOffer.Contract = contract;

contractOffer;

bool continueNegotiations = false;
playerReSignNegotiation.ActiveRequestID = -1;

// If the League does not have owner, do not process any negotiation.
// No-owner leagues are created from binary exporter simulations, where the Weeks are generated processing all the teams like CPU teams.
// Thus, we wanted to avoid any auto negotiation for all the teams.
if (PlayerSigningEval.FranchiseRef.LeagueOwner != null)
{
    if( !playerReSignNegotiation.Team.IsUserManaged() 
      || ( playerReSignNegotiation.Team.IsUserManaged() && 
            (  (teamRequest.Response == teamRequest.WithdrawResponse)
            || (teamRequest.Response == teamRequest.MakeOfferResponse)
            || (teamRequest.Response == teamRequest.SubmitResponse)
            || ( ( (teamRequest.Response == teamRequest.DefaultResponse) || (teamRequest.Response == null) ) && !playerReSignNegotiation.Team.TeamSettingRef.IsCPUReSignPlayersEnabled ) ) ) )
    {
        // is this the initial generated contract
        if(playerReSignNegotiation.InitialContract)
        {
            // inital resign response
            continueNegotiations = ProcessTeamInitialResponse(playerReSignNegotiation, teamRequest);
            playerReSignNegotiation.InitialContract = false;
        }
        else
        {
            // player wait
            continueNegotiations = ProcessTeamResponse(playerReSignNegotiation, teamRequest);
        }

        // remove the player if the team decided not to ReSign him
        if(continueNegotiations)
        {
            ReSign_PlayerRequest playerRequest = PlayerSigningEval.CreatePlayerRequest( playerReSignNegotiation );
            playerRequest.PlayerReSignNegotiation = playerReSignNegotiation;
            playerRequest.ReSignNegotiationManager = this;

            playerRequest.Issue(playerReSignNegotiation.Player);

            playerReSignNegotiation.ActiveRequestID = playerRequest.RequestId;
        }
        else
        {
            playerReSignNegotiation.NegotiationsEnded = true;
            playerReSignNegotiation.ReSignStatus = ReSignStatus.NotInterested;
            playerReSignNegotiation.ActiveRequestID = -1;
            EndReSignTransaction(playerReSignNegotiation);
        }
    }
}

if(activeReSignNegotiations.Population() != 0)
{
  UpdateReSignNegotiationRequests updateReSignRequestView = new UpdateReSignNegotiationRequests;
  updateReSignRequestView.PlayerSigningEval = PlayerSigningEval;
  updateReSignRequestView.TeamRequestManager = TeamRequestManager;
   updateReSignRequestView.PlayerGradeEval = PlayerGradeEval;
   updateReSignRequestView.ReSignNegotiationManager = this;
  updateReSignRequestView.Subjects = activeReSignNegotiations;
   updateReSignRequestView.DelegateTeamRequest = true;
  updateReSignRequestView.SendTeamRequests = true;
   updateReSignRequestView.Update();
}

if(activeReSignNegotiations.Population() != 0)
{
  UpdateReSignNegotiationRequests updateReSignRequestView = new UpdateReSignNegotiationRequests;
       updateReSignRequestView.PlayerSigningEval = PlayerSigningEval;
       updateReSignRequestView.TeamRequestManager = TeamRequestManager;
  updateReSignRequestView.PlayerGradeEval = PlayerGradeEval;
  updateReSignRequestView.ReSignNegotiationManager = this;
       updateReSignRequestView.Subjects = activeReSignNegotiations;
  updateReSignRequestView.DelegateTeamRequest = false;
  updateReSignRequestView.SendTeamRequests = true;
       updateReSignRequestView.Update();
}

// Handle for ReSign_PlayerRequest record.
bool result = false;

reSignNegotiation.ActiveRequestID = -1;

if( playerRequest.Response == PlayerSigningEval.PlayerAcceptResponseForm)
{
    // update the contract
    // This CreatePlayerContract call is also being called inside AcceptOffer
    PlayerContract contract = PlayerContractManager.CreatePlayerContract(reSignNegotiation.ContractSalary, reSignNegotiation.ContractBonus, reSignNegotiation.ContractLength);
    AcceptOffer(reSignNegotiation);
    result = false;
}
else if( playerRequest.Response == PlayerSigningEval.PlayerDeclineResponseForm)
{
    DeclineNegotiations(reSignNegotiation);
    result = false;
}
else if( playerRequest.Response == PlayerSigningEval.PlayerWaitResponseForm)
{
    //Send Player Waiting On Negotiation event
    WaitOnOffer(reSignNegotiation);
    result = true;
}
else if( playerRequest.Response == PlayerSigningEval.PlayerDelegateResponseForm)
{
    //Delegate the player's response
    result = DelegatePlayerResponse(reSignNegotiation);
}

result;

bool result = false;

// If the team submitted an offer, update the negotiation and continue the negotiation.
if ((teamRequest.Response == teamRequest.MakeOfferResponse) ||
    (teamRequest.Response == teamRequest.SubmitResponse))
{
    UIContractNegotiationForm contractForm = teamRequest.RequestForm;
    reSignNegotiation.ContractLength = contractForm.Length;
    reSignNegotiation.ContractSalary = contractForm.Salary;
    reSignNegotiation.ContractBonus = contractForm.Bonus;
    result = true;
}
// If the team chose to withdraw, end the negotiation.
else if (teamRequest.Response == teamRequest.WithdrawResponse)
{
    result = false;
}
// If the team chose to delegate, roll the dice to see if negotiations continue.
else if ((teamRequest.Response == null) ||
         (teamRequest.Response == teamRequest.DefaultResponse))
{
    int continueChance = reSignNegotiation.TeamReSignInterest;
    int continueValue = Random.GetRandomIntRange(0, 1);

    result = (continueValue < continueChance);
}

// Begin the re-sign transaction. Note that we don't check the result here because the negotiation
// will always be begun and ended regardless of what the team decides here.
BeginReSignTransaction(reSignNegotiation);

result;

bool result = false;

// If an offer response was chosen, update the offer and continue the negotiation.
if ((teamRequest.Response == teamRequest.MakeOfferResponse) || (teamRequest.Response == teamRequest.SubmitResponse))
{
   UIContractNegotiationForm contractForm = teamRequest.RequestForm;
   result = UpdateOffer(reSignNegotiation, contractForm.Salary, contractForm.Bonus, contractForm.Length);
}

// If the offer was withdrawn, log the decision and end the negotiation.
else if (teamRequest.Response == teamRequest.WithdrawResponse)
{
   PlayerSigningEval.LogTeamDecision(reSignNegotiation.Team, reSignNegotiation.Player, TeamDecision.Withdraw);
   result = false;
}

else if ((teamRequest.Response == null) || (teamRequest.Response == teamRequest.DefaultResponse))
{
   int interest = reSignNegotiation.TeamReSignInterest;
   int randomPct = Random.GetRandomIntRange( 0, 100 );
   int playerPercentage = reSignNegotiation.Player.PlayerPercentage;
   PositionE position = reSignNegotiation.Player.Position;

   if(position == PositionE.QB)
   {
      if(reSignNegotiation.Player.SuperBowlWins >= 2)
      {
         //increase
         IncreaseOffer(reSignNegotiation, 8);
         result = true;
      }
      else if(playerPercentage <= 25)
      {
         //increase
         IncreaseOffer(reSignNegotiation, 8);
         result = true;
      }
      else if(playerPercentage > 20)
      {
         // 30%keep, 70% increase
         if(randomPct < 30)
         {
            //keep
            PlayerSigningEval.LogTeamDecision(reSignNegotiation.Team, reSignNegotiation.Player,  TeamDecision.Wait);
            result = true;
         }
         else
         {
            //increase
            IncreaseOffer(reSignNegotiation, 5);
            result = true;
         }
      }
   }
   if(position == PositionE.FB || position == PositionE.K || position == PositionE.P)
   {
     if(playerPercentage <= 25)
          {
                //increase
           IncreaseOffer(reSignNegotiation, 7);
             result = true;
          }
      else if (playerPercentage >25)
        {
            WaitOnOffer(reSignNegotiation);
            result = true;
       }
   }
   else if(reSignNegotiation.Player.Age >= 32)
   {  
      //keep
      PlayerSigningEval.LogTeamDecision(reSignNegotiation.Team, reSignNegotiation.Player,  TeamDecision.Wait);
      result = true;
   }
   else if(playerPercentage <= 5)
   {  
      // 15%keep, 90% increase
      if(randomPct < 15)
      {
         //keep
         PlayerSigningEval.LogTeamDecision(reSignNegotiation.Team, reSignNegotiation.Player,  TeamDecision.Wait);
         result = true;
      }
      else
      {
         //increase
         IncreaseOffer(reSignNegotiation, 6);
         result = true;
      }
   }
   
   else if(playerPercentage <= 20)
   {
      // 20%keep, 80% increase
      if(randomPct < 20)
      {
         //keep
         PlayerSigningEval.LogTeamDecision(reSignNegotiation.Team, reSignNegotiation.Player,  TeamDecision.Wait);
         result = true;
      }
      else
      {
         //increase
         IncreaseOffer(reSignNegotiation, 5);
         result = true;
      }
   }
   else
   if(playerPercentage > 35)
   {
      //keep
      PlayerSigningEval.LogTeamDecision(reSignNegotiation.Team, reSignNegotiation.Player,  TeamDecision.Wait);
      result = true;
   }
}

result;

team.TEAM_RATINGOFF = CalculateOffensiveRating(team);
int offensiveScore = team.TEAM_RATINGOFF * OffensiveWeight;

team.TEAM_RATINGDEF = CalculateDefensiveRating(team); 
int defensiveScore = team.TEAM_RATINGDEF * DefensiveWeight;

team.TEAM_RATINGST = CalculateSpecialTeamRating(team);
int specialTeamsScore = team.TEAM_RATINGST * SpecialTeamsWeight;

int coachLevelScore = 0;
if (team.HeadCoach != null)
{
    coachLevelScore = team.HeadCoach.Level * 3 * CoachWeight;
}  

int teamHistoryScore = team.TeamHistory * TeamHistoryWeight;

int totalScore = (offensiveScore + defensiveScore + specialTeamsScore + coachLevelScore + teamHistoryScore) / (OffensiveWeight + DefensiveWeight + SpecialTeamsWeight + CoachWeight + TeamHistoryWeight);

totalScore;

string value = APlusGrade;

if(rank >= 31) 
{
value = FMinusGrade;
}
else if(rank >= 27) 
{
value = FGrade;
}
else if(rank >= 26)
{
value = FPlusGrade;
} 
else if(rank >= 25)
{
value = DMinusGrade;
}
else if(rank >=21) 
{
value = DGrade;
}
else if(rank >= 20) 
{
value = DPlusGrade;
}
else if(rank >= 19)
{
value = CMinusGrade;
}
else if(rank >=14)
{
value = CGrade;
}
else if(rank >= 13)
{
value = CPlusGrade;
}
else if(rank>= 12)
{
value = BMinusGrade;
}
else if(rank >= 8)
{
value = BGrade;
}
else if(rank >=7)
{
value = BPlusGrade;
}
else if(rank >= 6)
{
value = AMinusGrade;
}
else if(rank >= 1)
{
value = AGrade;
}

value;

Coach coach = event.Coach;
Team oldTeam = event.OldTeam;
Team newTeam = event.NewTeam;

// If a CPU coach was signed to a new team, apply that coach's scheme.
if (!coach.IsUserControlled && (newTeam != null) && (oldTeam != newTeam))
{
    ConvertTeamPlayerTypes(newTeam);
}

CoachManagerRef.AgeCoach(Subject);

Team nullTeam = null;
CoachRetirementEvalRef.EvaluateCoachForRetirement(Subject, nullTeam);

Player player = Subject;

// This is called for all team roster players, team practice squad players, and free agents.
// Since practice squad players are in the free agent array, don't call this twice for them.
if (player.ContractStatus == PlayerContractStatus.FreeAgent || Team != null)
{
    // Increment the player's age.
    PlayerManager.AgePlayer(player);

    // Handle the player on practice squad.
    if (player.WasOnPracticeSquadThisYear)
    {
        player.WasOnPracticeSquadThisYear = false;
        player.PracticeSquadYears = player.PracticeSquadYears + 1;
    }

    // If TeamManager is null, it means this player is in the free agent or
    // retirement pool for some reason. He needs to be removed from the
    // pools via validation and repair.
    if (TeamManager != null && Team != null)
    {
        // Advance the player's contract year.
        if (player.ContractStatus == PlayerContractStatus.Signed)
        {
            TeamManager.AdvancePlayerContractYear(Team, player);
        }
        else if (player.ContractStatus == PlayerContractStatus.PracticeSquad)
        {
            int playerSalary = SalaryMetrics.GetMarketContractAnnualSalary(player.Position, PlayerGradeEval.CalculateGrades(player, player.PlayerType), player.Age);
            PlayerContract contract = PlayerContractManager.CreatePlayerContract(playerSalary, 0, 2);
            TeamManager.SignPlayer(Team, player, contract);
        }
    }

    // Evaluate the player's retirement status.
    if ((player.ContractStatus == PlayerContractStatus.Signed) ||
        (player.ContractStatus == PlayerContractStatus.FreeAgent) ||
        (player.ContractStatus == PlayerContractStatus.Expiring))
    {
        PlayerRetirementEval.EvaluatePlayerForRetirement(Subject, Team);
    }

    // Clear the player's seasonal goal.
    player.SeasonalGoal = null;
}

if (IsPractice)
{
PracticeEvalRef.ProcessSeasonGameRequest(this);
}

// Set the season info.
SeasonInfo.CurrentStage = stage;
SeasonInfo.CurrentWeekType = weekType;
SeasonInfo.CurrentWeek = 0;

// Send the event.
BeginStageEvent.Year = SeasonInfo.CurrentYear;
BeginStageEvent.Stage = SeasonInfo.CurrentStage;
BeginStageEvent.Send();

// Look up the current week type.
// SeasonInfo.CurrentWeekType = GetWeekType(SeasonInfo.CurrentStage, SeasonInfo.CurrentWeek);

RosterInfo.MaxRosterSize = RosterInfo.EndOfWeekMaxRosterSize;

// Send the event.
BeginWeekEvent.Year = SeasonInfo.CurrentYear;
BeginWeekEvent.Stage = SeasonInfo.CurrentStage;
BeginWeekEvent.WeekType = SeasonInfo.CurrentWeekType;
BeginWeekEvent.Week = SeasonInfo.CurrentWeek;
BeginWeekEvent.Send();

SeasonInfoEvent.Send();

// Initialize the season info.
SeasonInfo.CurrentStage = SeasonStage.PreSeason;
SeasonInfo.CurrentWeekType = SeasonWeekType.PreSeason;
SeasonInfo.CurrentWeek = 0;

// Send the event.
BeginYearEvent.Year = SeasonInfo.CurrentYear;
BeginYearEvent.Send();

// Send the event.
EndStageEvent.Year = SeasonInfo.CurrentYear;
EndStageEvent.Stage = SeasonInfo.CurrentStage;
EndStageEvent.Send();

// Send the event.
EndWeekEvent.Year = SeasonInfo.CurrentYear;
EndWeekEvent.Stage = SeasonInfo.CurrentStage;
EndWeekEvent.WeekType = SeasonInfo.CurrentWeekType;
EndWeekEvent.Week = SeasonInfo.CurrentWeek;
EndWeekEvent.Send();

// Increment the week.
SeasonInfo.CurrentWeek = SeasonInfo.CurrentWeek + 1;

// Send the event.
EndYearEvent.Year = SeasonInfo.CurrentYear;
EndYearEvent.Send();

// Increment the year.
SeasonInfo.CurrentYear = SeasonInfo.CurrentYear + 1;
SeasonInfo.CurrentSeasonYear = SeasonInfo.CurrentSeasonYear + 1;

// Get the team info
Team team = Subject;

PracticeEvalRef.IssuePracticeGameRequest(team, true, !team.IsUserManaged());

if (OtherSubject.SeasonWinPct == Subject.SeasonWinPct)
{
  SeasonStandings.SortTeamsWithTieBreakers(Subject, OtherSubject, false);
}
else
{
  (OtherSubject.SeasonWinPct - Subject.SeasonWinPct);
}

if (OtherSubject.SeasonWinPct == Subject.SeasonWinPct)
{ 
  Env.SeasonStandingsRef.SortTeamsWithTieBreakers(Subject, OtherSubject, true);
}
else
{
  (OtherSubject.SeasonWinPct - Subject.SeasonWinPct);
}

Player player = Subject.Player;
Team oldTeam = null;
Team newTeam = null;

// Add the player to the free agent list.
FreeAgents.Append(player);

// Update the player's status to free agent.
PlayerManagerRef.UpdatePlayerContractStatus(player, oldTeam, newTeam, player.ContractStatus, PlayerContractStatus.FreeAgent, false, false);

Player player = Subject;

// Determine this player's overall pick.
int overallPick = DraftManagerRef.GetPlayerOverallDraftPick(player);

// Create a contract for this player based on his overall pick.
PlayerContract playerContract = PlayerContractManagerRef.CreateDraftedRookieContract(overallPick);

// Sign the player.
TeamManagerRef.SignPlayer(Team, player, playerContract);

int signingThreshold = PlayerSigningEval.AddedValueThreshold;
int capRoomImpact = 0;

if(Subject.Roster.Population() < RosterSize)
{
    signingThreshold = 0;
}

bool cutPlayerToMakeRoom = (Subject.Roster.Population() >= RosterSize);

//if a player will need to be cut to add a free agent to the roster
if(cutPlayerToMakeRoom)
{
    //Choose the least valueable player currently on the team to cut
    PlayersToCut = CutDayEval.GenerateCutList(Subject, 1);
    capRoomImpact = CutDayEval.SalaryCapManagerRef.GetPlayerReleaseNetSalaryCapRoomImpact(Subject, PlayersToCut[0]);
}

ContractOffer offer = PlayerSigningEval.GenerateInSeasonFreeAgentOffer(Subject, FreeAgents, signingThreshold, capRoomImpact);

if(offer != null)
{
    if(cutPlayerToMakeRoom)
    {
        PlayerManager.TeamManager.ReleasePlayer(Subject, PlayersToCut[0], false);
    }

    PlayerManager.SignPlayer(Subject, offer.Player, offer.Contract);
}

StaffHiring_FilterOffersForCoach FilterOffersForCoachView = new StaffHiring_FilterOffersForCoach;
StaffPersonContractOffer[] coachOfferList = null;

if(Subject.HeadCoach != null)
{
  if(Subject.HeadCoach.ContractStatus == StaffPersonContractStatus.Expiring)
  {
    if(StaffHiringEvalRef.OutstandingStaffPersonOffersList != null)
    {
      FilterOffersForCoachView.CoachRef = Subject.HeadCoach;
      FilterOffersForCoachView.Subjects = StaffHiringEvalRef.OutstandingStaffPersonOffersList;
      FilterOffersForCoachView.Update();
      coachOfferList = FilterOffersForCoachView.Results;
    }

    //Build and Issue Request
    if(coachOfferList != null)
    {
      if(coachOfferList.Population() > 0)
      {
        StaffHiringEvalRef.IssueEvaluateCoachOffersRequest(Subject.HeadCoach, coachOfferList, true, Subject);
      }
    }
  }
}

