// set the schemeMod
int schemeMod = ok;
Coach coach = contractOffer.StaffPerson;
Team team   = contractOffer.Team;

if(coach.DefensiveScheme != null && coach.OffensiveScheme != null)
{
  if ((coach.DefensiveScheme.Value == team.DefaultDefensiveScheme) || (coach.OffensiveScheme.Value == team.DefaultOffensiveScheme))
  {
        schemeMod = great;
  }
}

// set the popularityMod
int popularityMod = 0;
int popularity = contractOffer.Team.OverallPopularity;
if (popularity > 80)
{
    popularityMod = great;
}
else if (popularity > 60)
{
    popularityMod = good;
}
else if (popularity > 40)
{
    popularityMod = ok;
}
else
{
    popularityMod = poor;
}

// set the historyMod
