else if (history > 80)
{
    historyMod = good;
}
else if (history > 60)
{
    historyMod = ok;
}
else
{
    historyMod = poor;
}

// set the successMod
int successMod = 0;
int success = contractOffer.Team.CurSeasonLeagStanding;
if (success <= 10)
{
    successMod = great;
}
else if (success <= 16)
{
    successMod = good;
}
else if (success <= 24)
{
    successMod = ok;
}
else
{
    successMod = poor;
}


// set the talentMod
int talentMod = 0;
int talent = contractOffer.Team.TEAM_RATINGOVR;
if (talent > 80)
{
    talentMod = great;
}
else if (talent > 75)
{
    talentMod = good;
}
else if (talent > 70)
{
    talentMod = ok;
}
else
{
    talentMod = poor;
}


