if( IsDelegateResponseAvailable ) {
    request.ResponseForms.Append( CreateCoachResignOffers_DelegateResponse );
}

request.DefaultResponse     = CreateCoachResignOffers_DelegateResponse;

team.IsReSigning = true;

TeamRequestManager.IssueToOwner(team, request);
}

//Build and Issue Request
EvaluateCoachOffersRequest request = CreateEvaluateCoachOffersRequest();

request.OfferList = offerList;
request.RequestData = offerList;
request.coach = staffPerson;
request.StaffHiringEvalRef = this;
request.IsResponseRequired = true;
request.isReSigning = isReSigning;
request.reSigningTeam = reSigningTeam;

request.ResponseForms = new ResponseForm[2];
request.ResponseForms.Append( EvaluateOffers_SelectOfferCommand );
request.ResponseForms.Append( EvaluateOffers_IgnoreResponse );

request.SelectOfferResponse = EvaluateOffers_SelectOfferCommand;
request.IgnoreResponse      = EvaluateOffers_IgnoreResponse;
request.DefaultResponse     = EvaluateOffers_DelegateResponse;
request.DelegateResponse    = EvaluateOffers_DelegateResponse;

request.Issue(staffPerson);

StaffHiring_FilterOffersForCoach FilterOffersForStaffPersonView = new StaffHiring_FilterOffersForCoach;
StaffPersonContractOffer[] staffPersonOfferList = null;

if(OutstandingStaffPersonOffersList != null)
{
  FilterOffersForStaffPersonView.CoachRef = staffPerson;
  FilterOffersForStaffPersonView.Subjects = OutstandingStaffPersonOffersList;
  FilterOffersForStaffPersonView.Update();
  staffPersonOfferList = FilterOffersForStaffPersonView.Results;
}

//Build and Issue Request
if(staffPersonOfferList!= null)
{
  if(staffPersonOfferList.Population() > 0)
  {
    IssueEvaluateCoachOffersRequest(staffPerson, staffPersonOfferList, isResigning, reSigningTeam);
  }
