StaffHiringPhilosophy philosophy = team.Philosophy.StaffHiringPhilosophyRef;

int highestRatingToCurrentRatingPercentage = (highestRatedStaffRating * 100) / currentStaffRating;

bool prefercurrentstaff = false;

if(highestRatingToCurrentRatingPercentage >= philosophy.HighestStaffRatingComparedToCurrentStaffRatingPercentage)
{
prefercurrentstaff = false;
}
else
{
prefercurrentstaff = true;
}

prefercurrentstaff;

// If the "Delegate" response was chosen, update offers for the selected team.
if (request.Response == CreateCoachOffers_DelegateResponse && Franchise.SeasonInfo.IsStaffHiringPeriodActive)
{
    Team team = request.Team;

    CreateContractOffer(team, CoachPosition.HeadCoach);
    CreateContractOffer(team, CoachPosition.Scout);
    CreateContractOffer(team, CoachPosition.Trainer);
}

Team team = request.Team;

// If the "Delegate" response was chosen, update offers for the selected team.
if (request.Response == CreateCoachResignOffers_DelegateResponse && Franchise.SeasonInfo.IsStaffHiringPeriodActive)
{
    CreateResignContractOffer(team, CoachPosition.HeadCoach);
    CreateResignContractOffer(team, CoachPosition.Scout);
    CreateResignContractOffer(team, CoachPosition.Trainer);
}

ReleaseStaffWithNoOutstandingOffers(team);

bool acceptedOffer = false;

// If the "Delegate" response was chosen, update offers for the selected team.
if (request.Response == EvaluateOffers_DelegateResponse)
{
    if(request.TargetUser == null)
    {
    StaffPersonContractOffer offer = SelectOfferToAccept(request.coach, request.OfferList);
    if(offer != null)
    {
        acceptedOffer = true;
        TeamManagerRef.SignStaffPerson(offer.Team, offer, false);
    }
    }
}

if(request.isReSigning)
{
RemoveOutstandingOffers(request.coach);
ReleaseStaffWithNoOutstandingOffers(request.reSigningTeam);
}

TeamStats[] statsArray = null;

if (stage == SeasonStage.PreSeason)
{
    statsArray = TeamGameStatsPreSeason;
}
else
{
    statsArray = TeamGameStatsRegSeason;
}

statsArray;

// Update the player's team history.
player.PLYR_CONSECYEARSWITHTEAM = 0;

// If the player has been drafted, add him to the drafted players array.
if (contractStatus == PlayerContractStatus.Drafted)
{
    team.DraftedPlayers.Append(player);
}
// If the player has been signed, add him to the roster.
else if (contractStatus == PlayerContractStatus.Signed)
{
   team.Roster.Append(player);

    // Upadate player's Injured Reserve status and team's active roster size
    player.IsInjuredReserve = false;
    team.ActiveRosterSize = team.ActiveRosterSize + 1;
}

// If the player is in his last contract year, set his status to expiring.
if (player.ContractYear == player.ContractLength - 1)
{
    PlayerManager.UpdatePlayerContractStatus(player, team, team, player.ContractStatus, PlayerContractStatus.Expiring, false, false);
}
// Otherwise, increment the player's contract year and cap salary.
else
{
    player.ContractYear = player.ContractYear + 1;

    if (player.ContractYear == 1)
    {
        player.PLYR_CAPSALARY = player.ContractSalary1 + player.ContractBonus1;
    }
    else if (player.ContractYear == 2)
    {
        player.PLYR_CAPSALARY = player.ContractSalary2 + player.ContractBonus2;
    }
    else if (player.ContractYear == 3)
    {
        player.PLYR_CAPSALARY = player.ContractSalary3 + player.ContractBonus3;
    }
    else if (player.ContractYear == 4)
    {
        player.PLYR_CAPSALARY = player.ContractSalary4 + player.ContractBonus4;
    }
    else if (player.ContractYear == 5)
    {
        player.PLYR_CAPSALARY = player.ContractSalary5 + player.ContractBonus5;
    }
    else if (player.ContractYear == 6)
    {
        player.PLYR_CAPSALARY = player.ContractSalary6 + player.ContractBonus6;
    }
    else if (player.ContractYear == 7)
    {
        player.PLYR_CAPSALARY = player.ContractSalary7 + player.ContractBonus7;
    }
}

// Clear the player's trade status when his contract year advances.
player.TradeStatus = TradeStatus.None;

// Set the player's round and pick information.
player.PLYR_DRAFTROUND = currentRoundIndex + 1; 
player.PLYR_DRAFTPICK = (currentPickIndex % Franchise.Teams.Population()) + 1;
player.PLYR_DRAFTTEAM = team.PresentationId;

Team oldTeam = null;
PlayerContractStatus oldStatus = player.ContractStatus;
PlayerContractStatus newStatus = PlayerContractStatus.Drafted;

// Add the player to the team.
AddPlayer(team, player, newStatus);

// Update the player contract status.
PlayerManager.UpdatePlayerContractStatus(player, oldTeam, team, oldStatus, newStatus, false, false);

if(staffPerson.Position == CoachPosition.HeadCoach)
{
ReleaseCoach(team);
}
else if(staffPerson.Position == CoachPosition.Scout)
{
ReleaseScout(team);
}
else if(staffPerson.Position == CoachPosition.Trainer)
{
ReleaseTrainer(team);
}

NotifyRequest request = CreateFranchiseTagNotifyRequest();

request.Title = FranchiseTagNotifyRequestTitle;
request.Description = FranchiseTagNotifyRequestDescription;
request.RequestData = team;

request.Issue(player);

StaffEventNotificationRequest request = new StaffEventNotificationRequest ;

StaffEventNotificationRequest nullRequest = null;

if (request != nullRequest)
{
    request.RequestData = contractOffer;
    request.IsResponseRequired = false;
    
    request.ResponseForms = new ResponseForm[1];
    request.ResponseForms.Append( StaffHiringNotificationDismissResponse);

    request.DefaultResponse = StaffHiringNotificationDismissResponse;
    request.EventType = eventType;

    request.Issue(requestReceiver);
}

player.IsInjuredReserve = true;
team.ActiveRosterSize = team.ActiveRosterSize - 1;

//Build and send event
PlayerPlacedOnIREvent.player = player;
PlayerPlacedOnIREvent.team = team;
PlayerPlacedOnIREvent.Send();

// Zero out the coach's contract
Coach coach = team.HeadCoach;
Coach nullCoach = null;

if (coach != nullCoach)
{
    coach.ContractYearsRemaining = 0;

    // Remove the coach from the team.
    team.HeadCoach = null;

    // Add the coach to the free agent pool
    Franchise.FreeAgentCoaches.Append(coach);

    // Update the coach's contract status.
    Team newTeam = null;
    CoachManager.UpdateCoachContractStatus( coach, team, newTeam, StaffPersonContractStatus.FreeAgent);

    // Add the coach to the team's coach blacklist
    team.StaffPersonBlacklist.Append(coach);

    // send the notification
    StaffPersonContractOffer contract = new StaffPersonContractOffer;
    contract.StaffPerson = coach;
    contract.Length = 0;
    contract.Salary = 0;
    contract.Team = null;

    StaffEventType event = StaffEventType.Fired;
    IssueStaffEventNotificationRequest(contract, event, coach);
}

// Only apply cap penalties if the player is not demanding a release. If he demands a release,
// he does not get his remaining signing bonus.
bool applyPenalty = !player.IsDemandRelease;

PlayerContractStatus oldStatus = player.ContractStatus;

// Remove the player from the team.
RemovePlayer(team, player, applyPenalty);

// Update the player's contract status.
Team newTeam = null;
PlayerManager.UpdatePlayerContractStatus(player, team, newTeam, player.ContractStatus, PlayerContractStatus.FreeAgent, false, isDeadWeightCut);

// Clear the player's contract data.
player.ContractLength = 0;
player.ContractYear = 0;
player.ContractSalary0 = 0;
player.ContractSalary1 = 0;
player.ContractSalary2 = 0;
player.ContractSalary3 = 0;
player.ContractSalary4 = 0;
player.ContractSalary5 = 0;
player.ContractSalary6 = 0;
player.ContractSalary7 = 0;
player.ContractBonus0 = 0;
player.ContractBonus1 = 0;
player.ContractBonus2 = 0;
player.ContractBonus3 = 0;
player.ContractBonus4 = 0;
player.ContractBonus5 = 0;
player.ContractBonus6 = 0;
player.ContractBonus7 = 0;
player.ContractExtraYearOption = false;

// Add the player to the free agent pool.
if (oldStatus != PlayerContractStatus.FreeAgent && oldStatus != PlayerContractStatus.PracticeSquad)
{
    Franchise.FreeAgents.Append(player);
}

// Zero out the scout's contract
Scout scout = team.HeadScout;
Scout nullScout = null;

if (scout!= nullScout )
{
    scout.ContractYearsRemaining = 0;

    // Update the coach's contract status.
    Team newTeam = null;
    CoachManager.UpdateScoutContractStatus( scout, team, newTeam, StaffPersonContractStatus.FreeAgent);

    // Remove the coach from the team.
    team.HeadScout= null;

    // Add the scout to the free agent pool
    Franchise.FreeAgentScouts.Append(scout);

    // Add the scout to the team's staff blacklist
    team.StaffPersonBlacklist.Append(scout);
}

// Zero out the trainer's contract
Trainer trainer = team.HeadTrainer;
Trainer nullTrainer = null;

if (trainer != nullTrainer )
{
    trainer.ContractYearsRemaining = 0;

    // Update the coach's contract status.
    Team newTeam = null;
    CoachManager.UpdateTrainerContractStatus( trainer, team, newTeam, StaffPersonContractStatus.FreeAgent);

    // Remove the coach from the team.
    team.HeadTrainer = null;

    // Add the coach to the free agent pool
    Franchise.FreeAgentTrainers.Append(trainer);

    // Add the trainer to the team's staff blacklist
    team.StaffPersonBlacklist.Append(trainer);
}

// If the player has been drafted but not signed, remove him from the drafted players 
// array.
if (player.ContractStatus == PlayerContractStatus.Drafted)
{
    team.DraftedPlayers.FindAndErase(player) ;
}
// If the player was on a practice squad, remove him from the team's practice squad array.
else if (player.ContractStatus == PlayerContractStatus.PracticeSquad)
{
    team.PracticeSquad.FindAndErase(player);
    player.IsDemandRelease = false;
    player.IsInjuredReserve = false;
}
// Otherwise, remove the player from the team roster.
else
{
    team.Roster.FindAndErase(player);

    int trainingPlayerIndex = team.FocusTrainingList.Find(player);
    if(trainingPlayerIndex != -1)
    {
        team.FocusTrainingList.Erase(trainingPlayerIndex );
    }
 
    // If requested for a non-expiring contract, apply the appropriate cap penalty.
    if (applyPenalty && (player.ContractStatus != PlayerContractStatus.Expiring))
    {
        SalaryCapManagerRef.ApplyReleasePenalties(team, player);
    }

    // Clear team-specific flags.
    player.IsDemandRelease = false;
    player.IsInjuredReserve = false;
    
    // Subtract the player from the active roster size.
    team.ActiveRosterSize = team.ActiveRosterSize - 1;
}

if(player.IsInjuredReserve == true)
{
 player.IsInjuredReserve = false;
 team.ActiveRosterSize = team.ActiveRosterSize + 1;

 if (decrementIRRemovalCount && (SeasonInfoRef.CurrentStage != SeasonStage.OffSeason))
 {
  team.IRRemovalCount = team.IRRemovalCount - 1;
 }
}

if (player.ContractStatus == PlayerContractStatus.PracticeSquad)
{
    RemovePlayer(team, player, false);
    player.ContractStatus = PlayerContractStatus.FreeAgent;
    Team nullTeam = null;
    
    PlayerManager.UpdatePlayerContractStatus(player, team, nullTeam, PlayerContractStatus.PracticeSquad, PlayerContractStatus.FreeAgent, false, false);
}

// If the player's contract is expiring, create an entirely new contract.
if (player.ContractStatus == PlayerContractStatus.Expiring)
{
    // Assign the player contract data.
    player.ContractLength = contract.Length;
    player.ContractYear = contract.CurrentYear;
    player.ContractExtraYearOption = contract.ExtraYearOption; 

    // Assign the player salary.
    player.ContractSalary0 = contract.SalaryTable[0];
    player.ContractSalary1 = contract.SalaryTable[1];
    player.ContractSalary2 = contract.SalaryTable[2];
    player.ContractSalary3 = contract.SalaryTable[3];
    player.ContractSalary4 = contract.SalaryTable[4];
    player.ContractSalary5 = contract.SalaryTable[5];
    player.ContractSalary6 = contract.SalaryTable[6];
    player.ContractSalary7 = 0;

    // Assign the player bonus.
    player.ContractBonus0 = contract.BonusTable[0];
    player.ContractBonus1 = contract.BonusTable[1];
    player.ContractBonus2 = contract.BonusTable[2];
    player.ContractBonus3 = contract.BonusTable[3];
    player.ContractBonus4 = contract.BonusTable[4];
    player.ContractBonus5 = contract.BonusTable[5];
    player.ContractBonus6 = contract.BonusTable[6];
    player.ContractBonus7 = 0;
}
// Otherwise, use the current year as the first year of his new contract.
else
{
    int currentYear = player.ContractYear;

    // Assign the player contract data.
    player.ContractLength = contract.Length + 1;
    player.ContractYear = 0;
    player.ContractExtraYearOption = contract.ExtraYearOption;

    int currentYearSalary = 0;
    int currentYearBonus = 0;    

    if(currentYear == 0)
    {
        currentYearSalary = player.ContractSalary0;
        currentYearBonus = player.ContractBonus0;
    }
    else if(currentYear == 1)
    {
        currentYearSalary = player.ContractSalary1;
        currentYearBonus = player.ContractBonus1;
    }
    else if(currentYear == 2)
    {
        currentYearSalary = player.ContractSalary2;
        currentYearBonus = player.ContractBonus2;
    }
    else if(currentYear == 3)
    {
        currentYearSalary = player.ContractSalary3;
        currentYearBonus = player.ContractBonus3;
    }
    else if(currentYear == 4)
    {
        currentYearSalary = player.ContractSalary4;
        currentYearBonus = player.ContractBonus4;
    }
    else if(currentYear == 5)
    {
        currentYearSalary = player.ContractSalary5;
        currentYearBonus = player.ContractBonus5;
    }
    else if(currentYear == 6)
    {
        currentYearSalary = player.ContractSalary6;
        currentYearBonus = player.ContractBonus6;
    }
    else if(currentYear == 7)
    {
        currentYearSalary = player.ContractSalary7;
        currentYearBonus = player.ContractBonus7;
    }

    // Assign the player salary.
    player.ContractSalary0 = currentYearSalary;
    player.ContractSalary1 = contract.SalaryTable[0];
    player.ContractSalary2 = contract.SalaryTable[1];
    player.ContractSalary3 = contract.SalaryTable[2];
    player.ContractSalary4 = contract.SalaryTable[3];
    player.ContractSalary5 = contract.SalaryTable[4];
    player.ContractSalary6 = contract.SalaryTable[5];
    player.ContractSalary7 = contract.SalaryTable[6];

    // Assign the player bonus.
    player.ContractBonus0 = currentYearBonus;
    player.ContractBonus1 = contract.BonusTable[0];
    player.ContractBonus2 = contract.BonusTable[1];
    player.ContractBonus3 = contract.BonusTable[2];
    player.ContractBonus4 = contract.BonusTable[3];
    player.ContractBonus5 = contract.BonusTable[4];
    player.ContractBonus6 = contract.BonusTable[5];
    player.ContractBonus7 = contract.BonusTable[6];
}

// Update the player's contract status.
PlayerManager.UpdatePlayerContractStatus(player, team, team, player.ContractStatus, PlayerContractStatus.Signed, wasFranchiseTagged, false);

if (wasFranchiseTagged)
{
    // If the player was franchise tagged, set the team franchise tag player and send notification.
    team.FranchiseTaggedPlayer = player;
    IssueFranchiseTagNotifyRequest(team, player);
}

// Remove the coach from the team.
team.HeadCoach = null;

// Update the coach's contract status.
Team newTeam = null ;
CoachManager.UpdateCoachContractStatus(coach, team, newTeam, StaffPersonContractStatus.Retired) ;

if (team != null)
{
    // Restore the default team owner.
    team.Owner = team.DefaultOwner;

    owner.PrevTeamIndex = owner.TeamIndex;
    owner.TeamIndex = TeamIndex.None;
  
    // Add the user to the team blacklist.
    team.BlacklistUserIdLower = user.UserIdLower;
    team.BlacklistUserIdUpper = user.UserIdUpper;

    // Send the owner retirement event.
    RetireOwnerEvent event = RetireOwnerEventRef;
    event.Owner = owner;
    event.Team = team; 
    event.Send();

    // Clear the event data to prevent leaks.
    event.Owner = null;
    event.Team = null;
}

// Remove the player from the team (do not apply penalties).
RemovePlayer(team, player, false);

// Update the player's contract status.
Team newTeam = null;
PlayerManager.UpdatePlayerContractStatus(player, team, newTeam, player.ContractStatus, PlayerContractStatus.Retired, false, false);

// Clear the player's contract data.
player.ContractLength = 0;
player.ContractYear = 0;
player.ContractSalary0 = 0;
player.ContractSalary1 = 0;
player.ContractSalary2 = 0;
player.ContractSalary3 = 0;
player.ContractSalary4 = 0;
player.ContractSalary5 = 0;
player.ContractSalary6 = 0;
player.ContractSalary7 = 0;
player.ContractBonus0 = 0;
player.ContractBonus1 = 0;
player.ContractBonus2 = 0;
player.ContractBonus3 = 0;
player.ContractBonus4 = 0;
player.ContractBonus5 = 0;
player.ContractBonus6 = 0;
player.ContractBonus7 = 0;
player.ContractExtraYearOption = false;

// Add the player to the retired players list.
Franchise.RetiredPlayers.Append(player);

// Assign the coach contract data.
Coach coach = contract.StaffPerson;

coach.ContractYearsRemaining = contract.Length;
coach.ExperiencePoints = coach.ExperiencePoints + contract.ExperiencePoints;
coach.ContractLength = contract.Length;
coach.ContractSalary = contract.Salary;

bool isResign = team.HeadCoach == coach;

// Add coach to the team.
team.HeadCoach = coach;

// Find the coach in the free agent pool.
int coachIndex = Franchise.FreeAgentCoaches.Find(coach);

// If the coach is in the free agent pool, remove him.
if ((0 <= coachIndex) && (coachIndex < Franchise.FreeAgentCoaches.Population()))
{
    Franchise.FreeAgentCoaches.Erase(coachIndex);
}
// Otherwise, he is newly created. Set his previous status to "signed".
// TODO: Why wouldn't the previous status be "Free Agent" for created coaches?
else
{
    coach.ContractStatus = StaffPersonContractStatus.Signed;
}

// Set the contract to "Accepted".
contract.Status = ContractOfferStatus.Accepted;

// Update the coach contract status.
Team nullTeam = null;
CoachManager.UpdateCoachContractStatus(coach, nullTeam, team, StaffPersonContractStatus.Signed);

// Issue a notification.
// TODO: Notifications do not belong at this level. A listener for the coach contract status update
// should issue the notification. FranchiseRules modules should not know about them.
StaffEventType eventType = StaffEventType.Hired;
if (isResign)
{
    eventType = StaffEventType.ReSigned;
}
else if (IsValidationHire)
{
    eventType = StaffEventType.ValidationHired;
}

IssueStaffEventNotificationRequest(contract, eventType, team.Owner);

// Assign the player contract data.
player.ContractLength = contract.Length;
player.ContractYear = contract.CurrentYear;
player.ContractExtraYearOption = contract.ExtraYearOption;

// Assign the player salary.
player.ContractSalary0 = contract.SalaryTable[0];
player.ContractSalary1 = contract.SalaryTable[1];
player.ContractSalary2 = contract.SalaryTable[2];
player.ContractSalary3 = contract.SalaryTable[3];
player.ContractSalary4 = contract.SalaryTable[4];
player.ContractSalary5 = contract.SalaryTable[5];
player.ContractSalary6 = contract.SalaryTable[6];

// Assign the player bonus.
player.ContractBonus0 = contract.BonusTable[0];
player.ContractBonus1 = contract.BonusTable[1];
player.ContractBonus2 = contract.BonusTable[2];
player.ContractBonus3 = contract.BonusTable[3];
player.ContractBonus4 = contract.BonusTable[4];
player.ContractBonus5 = contract.BonusTable[5];
player.ContractBonus6 = contract.BonusTable[6];

// Assign the player a jersey number.
player.JerseyNum = FindAvailableJerseyNumber(team, player);

Team oldTeam = null;
PlayerContractStatus oldStatus = player.ContractStatus;
PlayerContractStatus newStatus = PlayerContractStatus.Signed;

// If the player was a free agent, remove him from the free agent pool.
if (oldStatus == PlayerContractStatus.FreeAgent)
{
    Franchise.FreeAgents.FindAndErase(player);
}
// If the player was on a practice squad, remove him from the free agent pool and
// from his old team.
else if (oldStatus == PlayerContractStatus.PracticeSquad)
{
    Franchise.FreeAgents.FindAndErase(player);
    
    oldTeam = FindTeamByTeamIndex(player.TeamIndex);
    if (oldTeam != null)
    {
        RemovePlayer(oldTeam, player, false);
    }
}
// If the player was drafted or previously signed, he is moving to the same team.
else if ((oldStatus == PlayerContractStatus.Drafted) ||
         (oldStatus == PlayerContractStatus.Expiring) ||
         (oldStatus == PlayerContractStatus.Signed))
{
    oldTeam = team;
}
// If the player was just created, add him to the free agent pool.
else if (oldStatus == PlayerContractStatus.Created)
{
    oldStatus = PlayerContractStatus.FreeAgent;
    player.ContractStatus = PlayerContractStatus.FreeAgent;
}

// Add the player to the team.
AddPlayer(team, player, newStatus);

// Update the player's contract status.
PlayerManager.UpdatePlayerContractStatus(player, oldTeam, team, oldStatus, newStatus, false, false);

PlayerContractStatus oldStatus = player.ContractStatus;
Team oldTeam = null;

if (oldStatus == PlayerContractStatus.Signed)
{
    // Player is being moved from roster to practice squad
    RemovePlayer(team, player, true);
    Franchise.FreeAgents.Append(player);
    oldTeam = team;
    
    // Clear the player's contract data now that any penalties have been applied.
    player.ContractLength = 0;
    player.ContractYear = 0;
    player.ContractSalary0 = 0;
    player.ContractSalary1 = 0;
    player.ContractSalary2 = 0;
    player.ContractSalary3 = 0;
    player.ContractSalary4 = 0;
    player.ContractSalary5 = 0;
    player.ContractSalary6 = 0;
    player.ContractSalary7 = 0;
    player.ContractBonus0 = 0;
    player.ContractBonus1 = 0;
    player.ContractBonus2 = 0;
    player.ContractBonus3 = 0;
    player.ContractBonus4 = 0;
    player.ContractBonus5 = 0;
    player.ContractBonus6 = 0;
    player.ContractBonus7 = 0;
    player.ContractExtraYearOption = false;
}
else
{
    // Player is a free agent being signed to a practice squad
    player.PLYR_CONSECYEARSWITHTEAM = 0;
}

team.PracticeSquad.Append(player);
player.ContractStatus = PlayerContractStatus.PracticeSquad;
player.IsInjuredReserve = false;
player.WasOnPracticeSquadThisYear = true;

PlayerManager.UpdatePlayerContractStatus(player, oldTeam, team, oldStatus, player.ContractStatus, false, false);

// Assign the scout contract data.
Scout scout= contract.StaffPerson;

scout.ContractYearsRemaining = contract.Length;
scout.ContractLength = contract.Length;
scout.ContractSalary = contract.Salary;

bool isResign = team.HeadScout == scout;

// Add scout to the team.
team.HeadScout = scout;

int scoutIndex = Franchise.FreeAgentScouts.Find(scout);

//Remove scout from freeAgentScouts unless they are newly created
if ((0 <= scoutIndex) && (scoutIndex < Franchise.FreeAgentScouts.Population()))
{
    Franchise.FreeAgentScouts.FindAndErase(scout);
}
else
{
    // this was a newly created scout so make him signed
    scout.ContractStatus = StaffPersonContractStatus.Signed;
}

contract.Status = ContractOfferStatus.Accepted;

Team nullTeam = null;
CoachManager.UpdateScoutContractStatus(scout, nullTeam, team, StaffPersonContractStatus.Signed);

StaffEventType eventType = StaffEventType.Hired;
if (isResign)
{
    eventType = StaffEventType.ReSigned;
}
else if (IsValidationHire)
{
    eventType = StaffEventType.ValidationHired;
}

IssueStaffEventNotificationRequest(contract, eventType, team.Owner);

StaffPerson staff = contract.StaffPerson;

if(staff.Position == CoachPosition.HeadCoach)
{
SignCoach(team, contract, IsValidationHire);
}
else if(staff.Position == CoachPosition.Trainer)
{
SignTrainer(team, contract, IsValidationHire);
}
else if(staff.Position == CoachPosition.Scout)
{
SignScout(team, contract, IsValidationHire);
}

// Assign the scout contract data.
Trainer trainer = contract.StaffPerson;

trainer.ContractYearsRemaining = contract.Length;
trainer.ContractLength = contract.Length;
trainer.ContractSalary = contract.Salary;

bool isResign = team.HeadTrainer == trainer;

// Add trainer to the team.
team.HeadTrainer = trainer;

int trainerIndex = Franchise.FreeAgentTrainers.Find(trainer);

//Remove trainer from freeAgentTrainers unless they are newly created
if ((0 <= trainerIndex) && (trainerIndex < Franchise.FreeAgentTrainers.Population()))
{
    Franchise.FreeAgentTrainers.FindAndErase(trainer);
}
else
{
    // this was a newly created scout so make him signed
    trainer.ContractStatus = StaffPersonContractStatus.Signed;
}

contract.Status = ContractOfferStatus.Accepted;

Team nullTeam = null;
CoachManager.UpdateTrainerContractStatus(trainer, nullTeam, team, StaffPersonContractStatus.Signed);

StaffEventType eventType = StaffEventType.Hired;
if (isResign)
{
    eventType = StaffEventType.ReSigned;
}
else if (IsValidationHire)
{
    eventType = StaffEventType.ValidationHired;
}

IssueStaffEventNotificationRequest(contract, eventType, team.Owner);

// If the trade is valid, set the new team and send an event.
if (pick.CurrentTeam == oldTeam)
{
    // Set the new team.
    pick.CurrentTeam = newTeam;

    // Send the event.
    TradeDraftPickEvent.OldTeam = oldTeam;
    TradeDraftPickEvent.NewTeam = newTeam;
    TradeDraftPickEvent.Pick = pick;
    TradeDraftPickEvent.Send();
    TradeDraftPickEvent.Clear();
}

// Note: All players involved in a trade must be removed from their teams prior
// to calling this method in case both teams have a full roster.

PlayerContractStatus oldStatus = player.ContractStatus;
PlayerContractStatus newStatus = PlayerContractStatus.Signed;

// Assign the player a jersey number prior to adding him to the roster.
player.JerseyNum = FindAvailableJerseyNumber(newTeam, player);

// Add the player to the new team.
AddPlayer(newTeam, player, newStatus);

// Update the player's contract status.
PlayerManager.UpdatePlayerContractStatus(player, oldTeam, newTeam, oldStatus, newStatus, false, false);

// Clear the player's signing bonus. The new team does not have to pay it. Note that we do this
// after updating the player's contract status so that the old team can remove it from their
// salary reserves properly.
player.ContractBonus0 = 0;
player.ContractBonus1 = 0;
player.ContractBonus2 = 0;
player.ContractBonus3 = 0;
player.ContractBonus4 = 0;
player.ContractBonus5 = 0;
player.ContractBonus6 = 0;
player.ContractBonus7 = 0;

// Null-check the team parameter.
Team nullTeam = null;

if (team != nullTeam)
{
    // Updates the team's contention phase depending on the results of the most recent season.
    PlayoffRoundType lastYearPlayoffResult = team.PlayoffRoundReached;
    TeamContentionPhase phase = TeamContentionPhase.Losing;

    // Team made the playoffs last year. 
    if (lastYearPlayoffResult != PlayoffRoundType.None)
    {
        // Team made the Conference game last year.  They're in a championship (win now) phase.
        if (lastYearPlayoffResult >= PlayoffRoundType.ConferenceChampionship)
        {
            phase = TeamContentionPhase.Championship;
        }
        // Team made a playoff run but didn't reach the Championship game.  They're in an playoff (balanced) phase.
        else
        {
            phase = TeamContentionPhase.Playoff;
        }
    }
    // Team missed the playoffs last year.
    else
    {
        // Team had a winning season last year.  They're in an playoff (balanced) phase.
        if (team.SeasonWinPct >= 500)
        {
            phase = TeamContentionPhase.Playoff;
        }
        // Team had a losing season last year.  They're in a losing (rebuilding) phase.
        else
        {
            phase = TeamContentionPhase.Losing;
        }
    }

    // Update the Team record with the new contention phase for the upcoming year or remainder of season.
    team.ContentionPhase = phase;
}

PopularityComponentTable newCurrentPopularity = new PopularityComponentTable;

int nationalModifier = team.CurrentPopularity.NationalPopularity;
int regionalModifier = team.CurrentPopularity.RegionalPopularity;
int localModifier = team.CurrentPopularity.LocalPopularity;

bool isWin = false;
if((team == seasonGame.AwayTeam && seasonGame.AwayScore > seasonGame.HomeScore) || (team == seasonGame.HomeTeam && seasonGame.HomeScore > seasonGame.AwayScore))
{
 isWin = true;
}

if(seasonGame.SeasonWeekType == SeasonWeekType.PreSeason)
{
       localModifier = localModifier + 0;
       regionalModifier = regionalModifier + 0;
       nationalModifier = nationalModifier + 0;
}

if(seasonGame.SeasonWeekType == SeasonWeekType.RegularSeason)
{      
 if(seasonGame.TimeOfDay > 1150 && seasonGame.DayOfWeek == DayOfWeek.Monday)
 {
  if(isWin == true)
  {
   localModifier = localModifier + 6;
   regionalModifier = regionalModifier + 5;
   nationalModifier = nationalModifier + 6;
  }
  else
  {
   localModifier = localModifier - 6;
   regionalModifier = regionalModifier - 5;
   nationalModifier = nationalModifier - 4;
  }
 }   
 else if(seasonGame.TimeOfDay > 1150 && seasonGame.DayOfWeek == DayOfWeek.Sunday)
 {
  if(isWin == true)
  {
   localModifier = localModifier + 6;
   regionalModifier = regionalModifier + 5;
   nationalModifier = nationalModifier + 6;
  }
  else
  {
   localModifier = localModifier - 6;
   regionalModifier = regionalModifier - 5;
   nationalModifier = nationalModifier - 4;
  }
 }
 else if(seasonGame.ThanksgivingFlag == true)
 {
  if(isWin == true)
  {
   localModifier = localModifier + 6;
   regionalModifier = regionalModifier + 5;
   nationalModifier = nationalModifier + 6;
  }
  else
  {
   localModifier = localModifier - 6;
   regionalModifier = regionalModifier - 5;
   nationalModifier = nationalModifier - 6;
  }
 }
 else if(seasonGame.SeasonWeek <= 17)
 {
  if(isWin == true)
  {
   localModifier = localModifier + 6;
   regionalModifier = regionalModifier + 5;
   nationalModifier = nationalModifier + 4;
  }
  else
  {
   localModifier = localModifier - 6;
   regionalModifier = regionalModifier - 5;
   nationalModifier = nationalModifier - 3;
  }
 }                     
}

if(seasonGame.SeasonWeekType == SeasonWeekType.WildcardPlayoff)
{
 if(isWin == true)
 {
  localModifier = (localModifier * 110) / 100;
  regionalModifier = (regionalModifier * 110) / 100;
  nationalModifier = (nationalModifier * 105) / 100;
 }
 else
 {
  localModifier = (localModifier * 105) / 100;
  regionalModifier = (regionalModifier * 104) / 100;
  nationalModifier = (nationalModifier * 101) / 100;
 }
}
                
if(seasonGame.SeasonWeekType == SeasonWeekType.DivisionalPlayoff)
{
 if(isWin == true)
 {
  localModifier = (localModifier * 115) / 100;
  regionalModifier = (regionalModifier * 115) / 100;
  nationalModifier = (nationalModifier * 110) / 100;
 }
 else
 {
  localModifier = (localModifier * 105) / 100;
  regionalModifier = (regionalModifier * 105) / 100;
  nationalModifier = (nationalModifier * 105) / 100;
 }
}
                
if(seasonGame.SeasonWeekType == SeasonWeekType.ConferencePlayoff)
{
 if(isWin == true)
 {
  localModifier = (localModifier * 120) / 100;
  regionalModifier = (regionalModifier * 120) / 100;
  nationalModifier = (nationalModifier * 115) / 100;
 }
 else
 {
  localModifier = (localModifier * 110) / 100;
  regionalModifier = (regionalModifier * 110) / 100;
  nationalModifier = (nationalModifier * 105) / 100;
 }
}

if(seasonGame.SeasonWeekType == SeasonWeekType.SuperBowl)
{
 if(isWin == true)
 {
  localModifier = 100;
  regionalModifier = 100;
  nationalModifier = 100;
 }
 else
 {
  localModifier = (localModifier * 110) / 100;
  regionalModifier = (regionalModifier * 110) / 100;
  nationalModifier = (nationalModifier * 110) / 100;
 }
}

if (team.PresentationId == 20)
{
 localModifier = 99;
}

// Update the base value from modifiers

int localPopularity = localModifier;
if(localPopularity > 100)
{
 localPopularity = 100;
}

if(localPopularity < 5)
{
 localPopularity = 5;
}

int nationalPopularity = nationalModifier;
if(nationalPopularity > 100)
{
 nationalPopularity = 100;
}

if(nationalPopularity < 5)
{
 nationalPopularity = 5;
}

int regionalPopularity = regionalModifier;
if(regionalPopularity > 100)
{
 regionalPopularity = 100;
}

if(regionalPopularity < 5)
{
 regionalPopularity = 5;
}

newCurrentPopularity.LocalPopularity = localPopularity;
newCurrentPopularity.NationalPopularity = nationalPopularity;
newCurrentPopularity.RegionalPopularity = regionalPopularity;

newCurrentPopularity;

TeamStats subjectASeasonStats = Subject.GetTeamCurrentSeasonStats();
TeamStats subjectBSeasonStats = OtherSubject.GetTeamCurrentSeasonStats();

(subjectASeasonStats.DEFPASSYARDS + subjectASeasonStats.DEFRUSHYARDS) - (subjectBSeasonStats.DEFPASSYARDS + subjectBSeasonStats.DEFRUSHYARDS);

TeamStats subjectASeasonStats = OtherSubject.GetTeamCurrentSeasonStats();
TeamStats subjectBSeasonStats = Subject.GetTeamCurrentSeasonStats();

(subjectASeasonStats.OFFPASSYARDS + subjectASeasonStats.OFFRUSHYARDS) - (subjectBSeasonStats.OFFPASSYARDS + subjectBSeasonStats.OFFRUSHYARDS);

int baseFunding = GetCityFundingBaseValue(city);
int multiplier = TeamPopularityToFundingMultiplierSpline.CalculateY(team.CurrentPopularity.NationalPopularity);

//Doing divide before multiply to stop from getting a value outside range of int.
baseFunding = (baseFunding / 100) * multiplier;

baseFunding;

int baseFunding = GetCityFundingBaseValue(city);
int multiplier = TeamPopularityToRebuildingFundingMultiplierSpline.CalculateY(team.CurrentPopularity.LocalPopularity);

//Doing divide before multiply to stop from getting a value outside range of int.
baseFunding = (baseFunding / 100) * multiplier;

baseFunding;

int cost = stadiumChoice.Cost;

int loan = cost - fundingAmount;

int loanPerWeek = 0;
if (loan > 0)
{
    int weeks = LoanYears * LoanWeeksPerYear;
    loanPerWeek = loan / weeks;
}
else
{
