  if(seasonGame.SeasonGameNum >= 8) 
  {
    percentTicketsSold = (percentTicketsSold * leagstandingMod) / 100;
  }

  if(team.IsRivalTeam(seasonGame.AwayTeam))
  {
    percentTicketsSold = (percentTicketsSold * 110) / 100;
  }

  if((seasonGame.DayOfWeek == DayOfWeek.Monday || seasonGame.DayOfWeek == DayOfWeek.Thursday || seasonGame.DayOfWeek == DayOfWeek.Sunday) && seasonGame.TimeOfDay > 1150)
  {
    percentTicketsSold = (percentTicketsSold * 110) / 100;
  }

  if(seasonGame.SeasonGameNum == 1)
  {
    percentTicketsSold = (percentTicketsSold * 125) / 100;
  }
}

else if(seasonGame.SeasonWeekType == SeasonWeekType.WildcardPlayoff
