   {
      TradeFeedbackText.Response1 = TradeFeedbackPhilPass;
      TradeFeedbackText.Response2 = TradeFeedbackPhilPass;
   }
   else if (tradePhilosophyValue < 90)
   {
      TradeFeedbackText.Response1 = TradeFeedbackPhilFail;
      TradeFeedbackText.Response2 = TradeFeedbackPhilFail;
   }
   else if (premiumValue > 200)
   {
      TradeFeedbackText.Response1 = TradeFeedbackGreatPremium;
      TradeFeedbackText.Response2 = TradeFeedbackGreatPremium;
   }
   else if (premiumValue < 150)
   {
      TradeFeedbackText.Response1 = TradeFeedbackDecentPremium;
      TradeFeedbackText.Response2 = TradeFeedbackDecentPremium;
    }

}

else if (value < 200 && isPlayer == true)
    {
      TradeFeedbackText.Response1 = TradeFeedbackBadValue;
    }

else if ( value > 500 && isPlayer == false)
    {
      TradeFeedbackText.Response1 = TradeFeedbackGoodDraftPick;
    }
else if (value > 200 && isPlayer == false)
    {
      TradeFeedbackText.Response1 = TradeFeedbackDecentDraftPick;
    }
else if (value < 200 && isPlayer == false)
    {
      TradeFeedbackText.Response1 = TradeFeedbackBadDraftPick;
    }


TradeFeedbackText;

// Process the request.
TradeNegotiationManagerRef.ProcessMakeOfferRequest(Negotiation, this);

// Clear the references.
TradeNegotiationManagerRef = null;
Negotiation = null;

Team teamA = Negotiation.PackageA.Team;
Team teamB = Subject;

// If this is not the offer team, sent a request to make an offer.
if (teamA != teamB)
{
    // Check to see if the player is blacklisted before sending the request
    if( !TradeEval.IsPlayerOnTeamTradeBlackList(teamB, Negotiation.PackageA) && teamB.IsUserManaged() == false)
    {
        Request = TradeEval.CreateMakeOfferRequest(teamB, Negotiation);
        TeamRequestManager.Issue(teamB, Request);
    }
}

bool processedTrade = true;

// If the trade is not valid, don't process it.
if (!IsTradeValid(packageA, packageB))
{
    processedTrade = false;
}

// If the trade is valid, execute it.
if (processedTrade)
{
    TradePlayersToTeam(packageA, packageB);

    // Construct and send the event.    
    TradeEvent.TeamA = packageA.Team;
    TradeEvent.TeamB = packageB.Team;
    TradeEvent.PackageA = packageA;
    TradeEvent.PackageB = packageB;
    TradeEvent.Send();

    // Clear the event members to prevent memory leaks.
    TradeEvent.TeamA = null;
    TradeEvent.TeamB = null;
    TradeEvent.PackageA = null;
    TradeEvent.PackageB = null;
}

processedTrade;

// Create the negotiation.
TradeNegotiation negotiation = TradeEvalRef.CreateDraftPickNegotiation(teamA, draftPickA);

// If the negotiation was successfully created, fill out the required members.
if (negotiation != null)
{
    // Add the negotiation to the list.
    TradeNegotiationList.Append(negotiation);

    // Set the draft active flag. This prevents the negotiation from being processed until
    // the team is ready to make a draft selection.
    negotiation.IsDraftActive = true;

    // Send requests to all other teams.
    TradeMakeOfferRequestTeams view = new TradeMakeOfferRequestTeams;
    {
        view.Subjects = Franchise.Teams;
        view.Negotiation = negotiation;
        view.TeamRequestManager = TeamRequestManagerRef;
        view.TradeEval = TradeEvalRef;
        view.Update();
    }
}

// Return the negotiation object.
negotiation;

// Create the negotiation.
TradeNegotiation negotiation = TradeEvalRef.CreatePlayerNegotiation(teamA, playerA);

// If the negotiation was successfully created, fill out the required members.
if (negotiation != null)
{
    // Add the negotiation to the list.
    TradeNegotiationList.Append(negotiation);

    // Send requests to all other teams.
    TradeMakeOfferRequestTeams view = new TradeMakeOfferRequestTeams;
    {
        view.Subjects = Franchise.Teams;
        view.Negotiation = negotiation;
        view.TeamRequestManager = TeamRequestManagerRef;
        view.TradeEval = TradeEvalRef;
        view.Update();
    }

    // Send the trade proposal event.
    Trade_TradeBlockEvent event = new Trade_TradeBlockEvent;
    {
        event.PlayerA = playerA;
        event.TeamA = teamA;
        event.Send();
    }
}

// Return the negotiation object.
negotiation;

// Create the negotiation.
TradeNegotiation negotiation = TradeEvalRef.CreateTeamNegotiation(packageA, packageB);

// If the negotiation is valid, proceed.
if (negotiation != null)
{
    // Issue requests to both teams.
    negotiation.RequestA = IssueNegotiationUpdateRequest(packageA.Team, negotiation);
    negotiation.RequestB = IssueNegotiationAcceptRequest(packageB.Team, negotiation);

    // Add the negotiation to the list.
    TradeNegotiationList.Append(negotiation);

    // send a trade proposal event
    Trade_TradeProposalEvent event = new Trade_TradeProposalEvent;
    {
        event.TeamA = negotiation.PackageA.Team;
        event.PackageA = negotiation.PackageA;
        event.RequestA = negotiation.RequestA;
        event.TeamB = negotiation.PackageB.Team;
        event.PackageB = negotiation.PackageB;
        event.RequestB = negotiation.RequestB;
        event.isModifiedProposal = false;
        event.isCounterProposal = false;
        event.isWithdrawnProposal = false;
        event.isTradeAccepted = false;
        event.isTradeFinished = false;
        event.TradeNegotiation = negotiation;

        event.Send();
    }
}

// Return the negotiation object.
negotiation;

bool canExecute = false;

// During the season, we need to make sure that both teams have either completed their game for this
// week, or that neither team has completed their game for this week. Otherwise, a player could play
// two games in one week.
if (Franchise.SeasonInfo.CurrentStage != SeasonStage.OffSeason)
{
    // Retrieve the teams.
    Team teamA = packageA.Team;
    Team teamB = packageB.Team;

    // Retrieve the current week number.
    int currentWeek = Franchise.SeasonInfo.CurrentWeek;

    if (Franchise.SeasonInfo.CurrentStage == SeasonStage.PreSeason && currentWeek == 0 && Franchise.SeasonInfo.CurrentYear == 0)
    {
        canExecute = false;
    }
    else
    {
        TeamStats[] statsA = teamA.GetTeamStatsForStage(Franchise.SeasonInfo.CurrentStage);
        TeamStats[] statsB = teamB.GetTeamStatsForStage(Franchise.SeasonInfo.CurrentStage);

        // If both teams have game stat arrays, proceed.
        if ((statsA != null) && (statsB != null))
        {
            // If neither team has played their game this week, the trade can be executed.
            if ((statsA[currentWeek] == null) && (statsB[currentWeek] == null))
            {
                canExecute = true;
            }
            // If both teams have played their game this week, the trade can be executed.
            else if ((statsA[currentWeek] != null) && (statsB[currentWeek] != null))
            {
                canExecute = true;
            }
        }
    }
}
// During the offseason, all trades can execute immediately.
else
{
    canExecute = true;
}

// Return the flag.
canExecute;

bool tradeExecuted = false;

// If the negotiation has not yet been processed, do so.
if (!negotiation.IsProcessed)
{
    TeamTradePackage packageA = negotiation.PackageA;
    TeamTradePackage packageB = negotiation.PackageB;

    // Remove the negotiation from the list to prevent re-entrancy issues.
    TradeNegotiationList.FindAndErase(negotiation);
    negotiation.IsProcessed = true;

    // If the trade was accepted, execute it.
    if (negotiation.Response == TradeResponse.Accept)
    {
        Team teamA = packageA.Team;
        Team teamB = packageB.Team;

        // Set both packages as accepted.
        packageA.Response = TradeResponse.Accept;
        packageB.Response = TradeResponse.Accept;

        // Execute the trade.
        tradeExecuted = TradeManagerRef.ExecuteTrade(packageA, packageB);

        // If the trade was executed, notify the teams of success.
        if (!tradeExecuted)
        {
            negotiation.Response = TradeResponse.Decline;
        }

        // Issue notifications to both teams.
        IssueTradeNegotiationNotification(teamA, negotiation);
        IssueTradeNegotiationNotification(teamB, negotiation);
    }
    // If the negotiation failed and was team-to-team, notify both teams of failure.
    else if ((negotiation.PackageA != null) && (negotiation.PackageB != null))
    {
        Team teamA = negotiation.PackageA.Team;
        Team teamB = negotiation.PackageB.Team;
    
    // Trade was declined by team A
    if(packageA.Response == TradeResponse.Decline)
    {
      //Issue notification to teamB
      IssueTradeNegotiationNotification(teamB, negotiation);
    }
    else if(packageB.Response == TradeResponse.Decline)
    {
      //Issue notification to teamB
      IssueTradeNegotiationNotification(teamA, negotiation);
    }
    
        // Set package A response.
        if (packageA.Response == TradeResponse.None)
        {
            packageA.Response = TradeResponse.Decline;
        }

        // Set package B response.
        if (packageB.Response == TradeResponse.None)
        {
            packageB.Response = TradeResponse.Decline;
        }  
    }

    // Dismiss all pending team requests.
    negotiation.DismissPendingTeamRequests();
    negotiation.PendingTeamRequestListB = null;

    // If there were multiple teams involved in the trade, notify them of failure.
    if (negotiation.TeamTradePackageListB != null)
    {
        IssueTradeNegotiationNotificationsView view = new IssueTradeNegotiationNotificationsView;
        {
            view.PackageA = packageA;
            view.PackageB = packageB;
            view.Negotiation = negotiation;
            view.TradeNegotiationManager = this;
            view.Subjects = negotiation.TeamTradePackageListB;
            view.Update();
        }
    }
}

// Clear any outstanding requests on this negotiation.
negotiation.RequestA = null;
negotiation.RequestB = null;

// Return true if the trade was executed.
tradeExecuted;

// If the pick moved from one team to another, invalidate any trade negotiations involving the pick.
if ((event.OldTeam != null) && (event.OldTeam != event.NewTeam))
{
    TradeEvalRef.HandleDraftPickMove(event.Pick, event.OldTeam, TradeNegotiationList);
}

// If the player moved from a valid team to a new team, make sure any trade negotiations
// involving that player are invalidated.
if ((event.OldTeam != null) && (event.OldTeam != event.NewTeam))
{
    TradeEvalRef.HandlePlayerMove(event.Player, event.OldTeam, TradeNegotiationList);
}

TradeNegotiationRequest request = TradeEvalRef.CreateTradeNegotiationRequest();

// Set the request settings.
request.IsResponseRequired = true;
request.IsConnectionRequired = false;
request.IsBlocking = false;

// Set the request data.
request.Team = team;
request.RequestData = negotiation;
request.Negotiation = negotiation;
request.NegotiationManager = this;

// Set the response forms.
request.ResponseForms = new ResponseForm[4];
request.ResponseForms.Append(TradeNegotiation_AcceptResponse);
request.ResponseForms.Append(TradeNegotiation_DeclineResponse);
request.ResponseForms.Append(TradeNegotiation_CounterResponse);
if (IsDelegateResponseAvailable)
{
    request.ResponseForms.Append(TradeNegotiation_DelegateResponse);
}

// Set the default response.
request.DefaultResponse = TradeNegotiation_DelegateResponse;

// Issue the request.
TeamRequestManagerRef.Issue(team, request);

request;

TradeNegotiationRequest request = TradeEvalRef.CreateTradeNegotiationRequest();

// Set the request settings.
request.IsResponseRequired = true;
request.IsConnectionRequired = false;
request.IsBlocking = false;

// Set the request data.
request.Team = team;
request.RequestData = negotiation;
request.Negotiation = negotiation;
request.NegotiationManager = this;

// Set the response forms.
request.ResponseForms = new ResponseForm[2];

// If there is no pending team list, this is a team-to-team negotiation. Allow the team to
// modify their offer.
if (negotiation.PendingTeamListB == null)
{
    request.ResponseForms.Append(TradeNegotiation_ModifyResponse);
}

// Always allow the team to withdraw their offer.
request.ResponseForms.Append(TradeNegotiation_WithdrawResponse);

// Issue the request.
TeamRequestManagerRef.Issue(team, request);

request;

// If notifications are enabled, issue the notification.
if (IsNotificationEnabled)
{
    TradeNegotiationRequest request = TradeEvalRef.CreateTradeNegotiationRequest();

    // Set the request settings.
    request.IsResponseRequired = false;
    request.IsConnectionRequired = false;
    request.IsBlocking = false;
    request.Priority = 25;

    // Set the request data.
    request.Team = team;
    request.RequestData = negotiation;
    request.Negotiation = negotiation;
    
    if(!IsDelegateResponseAvailable)
    {
    request.ResponseForms = new ResponseForm[1];
    request.ResponseForms.Append( TradeNegotiation_AcknowledgeResponse);

    request.DefaultResponse = TradeNegotiation_AcknowledgeResponse;
    }

    // Issue the request.
    TeamRequestManagerRef.Issue(team, request);
}

if (negotiation != null)
{
    // If the select response was selected, set the response to accept.
    if (request.Response == request.AcceptResponse)
    {
        negotiation.Response = TradeResponse.Accept;
    }
    // If the decline response was selected, set the response to decline.
    else if (request.Response == request.DeclineResponse)
    {
        negotiation.Response = TradeResponse.Decline;
    }
    // If the delegate response was selected, set the negotiation to be delegated
    // at the end of the week. Note that we don't process it immediately because
    // all offers might not yet be in.
    else if (request.Response == request.DelegateResponse)
    {
        negotiation.IsDelegated = true;
    }

    // If a response was selected, end negotiations.
    if (negotiation.Response != TradeResponse.None)
    {
        bool tradeExecuted = EndTradeNegotiation(negotiation);
    }
    // If the negotiation is to be delegated and there are no remaining teams, process the negotiation.
    else if (negotiation.IsDelegated && !negotiation.IsDraftActive && (negotiation.PendingTeamListB.Population() == 0))
    {
        ProcessNegotiation(negotiation);
    }
}

bool offerProcessed = false;

if (negotiation != null)
{
    TeamTradePackage packageB = null;

    // If delegate was selected, run the CPU logic to create an offer. This will return null if no
    // offer is created.
    if (request.Response == request.DelegateResponse)
    {
        packageB = TradeEvalRef.CreateTradeOffer(negotiation.PackageA, request.TeamB);
    }
    // If offer was selected, use the offered package.
    else if (request.Response == request.OfferResponse)
    {
        packageB = request.PackageB;
    }

    // If a package was offered, add it to the negotiation and send an event.
    if (packageB != null)
    {
        // Add the package the negotiation.
        negotiation.TeamTradePackageListB.Append(packageB);

        // Send a trade proposal event.
        Trade_TradeProposalEvent event = new Trade_TradeProposalEvent;
        {
            if (negotiation.PackageA != null)
            {
                event.TeamA = negotiation.PackageA.Team;
            }
            event.PackageA = negotiation.PackageA;
            event.RequestA = null;
            event.TeamB = request.TeamB;
            event.PackageB = packageB;
            event.RequestB = null;
            event.TradeNegotiation = negotiation;
            event.isCounterProposal = false;
            event.isModifiedProposal = false;
            event.isWithdrawnProposal = false;
            event.isTradeAccepted = false;
            event.isTradeFinished = false;
            event.Send();
        }

        // If this was the first package offered, send a request to team A.
        if (negotiation.TeamTradePackageListB.Population() == 1)
        {
    bool canTradePlayer = true;
            if (negotiation.PackageA != null && negotiation.PackageA.Team != null && negotiation.PackageA.Team.TeamSettingRef != null)
          {
            canTradePlayer = negotiation.PackageA.Team.TeamSettingRef.IsTradeAndFreeAgencyEnabled;
        }
    
        if (negotiation.PackageB != null && negotiation.PackageB.Team != null && negotiation.PackageB.Team.TeamSettingRef != null)
        {
            canTradePlayer = canTradePlayer  && negotiation.PackageB.Team.TeamSettingRef.IsTradeAndFreeAgencyEnabled;
        }

          if (canTradePlayer)
              {
                TradeAcceptOfferRequest acceptRequest = TradeEvalRef.CreateTradeAcceptOfferRequest(negotiation);
                TeamRequestManagerRef.Issue(negotiation.PackageA.Team, acceptRequest);
         }
        }
    }
    // If the team is user-controlled, add the player to the blacklist.
    else if (request.TeamB.HeadCoach == request.TeamB.UserCharacter)
    {
        TradeEvalRef.AddPlayerToTeamTradeBlackList(request.TeamB, negotiation.PackageA);
    }

    // Remove team B and the request from the pending lists.
    negotiation.PendingTeamListB.FindAndErase(request.TeamB);
    negotiation.PendingTeamRequestListB.FindAndErase(request);

    // If the negotiation is to be delegated and all teams have responded, process the negotiation.
    if (negotiation.IsDelegated && !negotiation.IsDraftActive && (negotiation.PendingTeamListB.Population() == 0))
    {
        ProcessNegotiation(negotiation);
    }
    // If a human user made an offer, issue an update request so that he can withdraw it later.
    else if ((packageB != null) && (request.TargetUser != null))
    {
        Request teamRequest = IssueNegotiationUpdateRequest(request.TeamB, negotiation);

        // If the team request is valid, store it in the pending request list.
        if ((negotiation.PendingTeamRequestListB != null) && (teamRequest != null))
        {
            negotiation.PendingTeamRequestListB.Append(teamRequest);
        }
    }

    // Mark the offer as processed.
    offerProcessed = true;
}

offerProcessed;

// If the negotiation has not yet been processed, do so now.
if (!negotiation.IsProcessed)
{
    // Select the best offer.
    negotiation.PackageB = TradeEvalRef.EvaluateTradeOffers(negotiation.PackageA, negotiation.TeamTradePackageListB);

    // If an offer was selected, execute the trade.
    if (negotiation.PackageB != null)
    {
        // Set the response to accepted.
        negotiation.Response = TradeResponse.Accept;

        // If the trade can be executed, do so. Otherwise, wait until the end of the week after
        // both teams have played their game.
        if (CanExecuteTrade(negotiation.PackageA, negotiation.PackageB))
        {
            bool tradeExecuted = EndTradeNegotiation(negotiation);
        }
    }
    // Otherwise, end negotiations immediately.
    else
    {
        negotiation.Response = TradeResponse.Decline;
        bool tradeExecuted = EndTradeNegotiation(negotiation);
    }
}

TradeNegotiation negotiation = request.Negotiation;

if ( negotiation != null )
{
 // If this is a league-wide negotiation, remove the request from the pending request list.
 if (negotiation.PendingTeamRequestListB != null)
 {
  negotiation.PendingTeamRequestListB.FindAndErase(request);
 }

 // If the negotiation has not yet been resolved, proceed. Otherwise, this is simply the
 // last team's request being dismissed.
 if (negotiation.Response == TradeResponse.None)
 {
  // If the trade was modified, re-issue trade requests.
  if ((request.Response == TradeNegotiation_ModifyResponse) ||
  (request.Response == TradeNegotiation_CounterResponse))
  {
   Team teamA = negotiation.PackageA.Team;
   Team teamB = negotiation.PackageB.Team;

   // If team A responded, dismiss request B and re-issue requests.
   if (request.Team == teamA)
   {
    if (negotiation.RequestB != null)
    {
     negotiation.RequestB.Dismiss();
    }

    negotiation.RequestA = IssueNegotiationUpdateRequest(teamA, negotiation);
    negotiation.RequestB = IssueNegotiationAcceptRequest(teamB, negotiation);
   }
   // If team B responded, dismiss request A and re-issue requests.
   else if (request.Team == teamB)
   {
    if (negotiation.RequestA != null)
    {
     negotiation.RequestA.Dismiss();
    }

    negotiation.RequestA = IssueNegotiationAcceptRequest(teamA, negotiation);
    negotiation.RequestB = IssueNegotiationUpdateRequest(teamB, negotiation);
   }

   // Send an event for the updated trade proposal.
   Trade_TradeProposalEvent event = new Trade_TradeProposalEvent;
   if (event != null)
   {
    if (negotiation.PackageA != null)
    {
     event.TeamA = negotiation.PackageA.Team;
    }
    
    event.PackageA = negotiation.PackageA;
    event.RequestA = negotiation.RequestA;
    
    if (negotiation.PackageB != null)
    {
     event.TeamB = negotiation.PackageB.Team;
    }
    
    event.PackageB = negotiation.PackageB;
    event.RequestB = negotiation.RequestB;
    event.isCounterProposal = (request.Response == TradeNegotiation_CounterResponse);
    event.isModifiedProposal = (request.Response == TradeNegotiation_ModifyResponse);
    event.isWithdrawnProposal = false;
    event.isTradeAccepted = false;
    event.isTradeFinished = false;
    event.TradeNegotiation = negotiation;

    event.Send();
   }
  }  
  else if (request.Response == TradeNegotiation_DeclineResponse)// If the trade was declined, end the negotiation.
  {
   negotiation.Response = TradeResponse.Decline;

   // Send an event for the trade execution. Note that the trade can fail even if
   // it was accepted.
   Trade_TradeProposalEvent event = new Trade_TradeProposalEvent;
   if (event != null)
   {
    if (negotiation.PackageA != null)
    {
     event.TeamA = negotiation.PackageA.Team;
    }
    
    event.PackageA = negotiation.PackageA;
    event.RequestA = negotiation.RequestA;
    
    if (negotiation.PackageB != null)
    {
     event.TeamB = negotiation.PackageB.Team;
    }
    
    event.PackageB = negotiation.PackageB;
    event.RequestB = negotiation.RequestB;
    event.isCounterProposal = false;
    event.isModifiedProposal = false;
    event.isWithdrawnProposal = false;
    event.isTradeAccepted = false;
    event.isTradeFinished = true;
    event.TradeNegotiation = negotiation;

    event.Send();
   }
   
   Team teamA = negotiation.PackageA.Team;
   Team teamB = negotiation.PackageB.Team;
   
   // If the trade was declined by team A, notify team B of their decision.
   if (request.Team == teamA)
   {
    negotiation.PackageA.Response = TradeResponse.Decline;
   }
   else if (request.Team == teamB)
   {
    negotiation.PackageB.Response = TradeResponse.Decline;
   }
  }
  else if (request.Response == TradeNegotiation_WithdrawResponse)// If the trade was withdrawn, update the negotiation accordingly.
  {
   // If this is a team-to-team negotiation, end the negotiation.
   if (negotiation.TeamTradePackageListB == null)
   {
    negotiation.Response = TradeResponse.Decline;
   }   
   else if (negotiation.PendingTeamRequestListB != null)// If this is a league-wide negotiation, remove the team's offer.
   {
    negotiation.RemoveTeamTradePackage(request.Team);
   }

   // Send an event for the trade execution. Note that the trade can fail even if
   // it was accepated.
   Trade_TradeProposalEvent event = new Trade_TradeProposalEvent;
   if (event != null)
   {
    if (negotiation.PackageA != null)
    {
     event.TeamA = negotiation.PackageA.Team;
    }
    
    event.PackageA = negotiation.PackageA;
    event.RequestA = negotiation.RequestA;
    
    if (negotiation.PackageB != null)
    {
     event.TeamB = negotiation.PackageB.Team;
    }
    
    event.PackageB = negotiation.PackageB;
    event.RequestB = negotiation.RequestB;
    event.isCounterProposal = false;
    event.isModifiedProposal = false;
    event.isWithdrawnProposal = true;
    event.isTradeAccepted = false;
    event.isTradeFinished = true;
    event.TradeNegotiation = negotiation;

    event.Send();
   }
  }
  else if (request.Response == TradeNegotiation_AcceptResponse)// If the trade was accepted, end the negotiation with acceptance.
  {
   negotiation.Response = TradeResponse.Accept;

   // Send an event for the trade execution. Note that the trade can fail even if
   // it was accepated.
   Trade_TradeProposalEvent event = new Trade_TradeProposalEvent;
   if (event != null)
   {
    if (negotiation.PackageA != null)
    {
     event.TeamA = negotiation.PackageA.Team;
    }
    
    event.PackageA = negotiation.PackageA;
    event.RequestA = negotiation.RequestA;
    
    if (negotiation.PackageB != null)
    {
     event.TeamB = negotiation.PackageB.Team;
    }
    
    event.PackageB = negotiation.PackageB;
    event.RequestB = negotiation.RequestB;
    event.isCounterProposal = false;
    event.isModifiedProposal = false;
    event.isWithdrawnProposal = false;
    event.isTradeAccepted = true;
    event.isTradeFinished = true;
    event.TradeNegotiation = negotiation;

    event.Send();
   }
  }
  // If the trade was delegated, decline the offer when both teams are User-Managed or use CPU logic to determine whether to accept in other case.
  else if (request.Response == TradeNegotiation_DelegateResponse)
  {
   Team teamA = negotiation.PackageA.Team;
   Team teamB = negotiation.PackageB.Team;

   int response = TradeResponse.Decline;
   if (!teamA.IsUserManaged() || !teamB.IsUserManaged())
   {
    // Evaluate the trade offer using CPU logic.
    response = TradeEvalRef.EvaluateTradeOffer(request.Team, negotiation.PackageA, negotiation.PackageB);
   }

   // If the trade was accepted, end the negotiation.
   if (response == TradeResponse.Accept)
   {
    negotiation.Response = TradeResponse.Accept;
   }
   // If the trade was declined by team B, notify team A of their decision.
   else if (request.Team == teamB)
   {
    bool issueNotification = true;
    if (negotiation.RequestA != null)
    {
     // If RequestA is ModifyResponse or CounterResponse, we do not want to issue an "acknowledge" into TeamA corresponding to the old negotiation request.
     issueNotification = ((negotiation.RequestA.Response != TradeNegotiation_ModifyResponse) && 
           (negotiation.RequestA.Response != TradeNegotiation_CounterResponse));
     negotiation.RequestA.Dismiss();
    }

    if (issueNotification)
    {
     IssueTradeNegotiationNotification(teamA, negotiation);
    }
    
    negotiation.RequestA = null;
    negotiation.RequestB = null;
   }
   // Otherwise, end the negotiation.
   else
   {
    negotiation.Response = response;
   }
  }

  // If the trade has no response, wait for further action.
  if (negotiation.Response == TradeResponse.None)
  {
   // Do nothing. Wait for further action.
  }
  // If the trade was accepted, attempt to execute the trade.
  else if (negotiation.Response == TradeResponse.Accept)
  {
   if (CanExecuteTrade(negotiation.PackageA, negotiation.PackageB))
   {
    EndTradeNegotiation(negotiation);

    // Send an event for the trade execution. Note that the trade can fail even if
    // it was accepated.
    Trade_TradeProposalEvent event = new Trade_TradeProposalEvent;
    if (event != null)
    {
     if (negotiation.PackageA != null)
     {
      event.TeamA = negotiation.PackageA.Team;
     }
     
     event.PackageA = negotiation.PackageA;
     event.RequestA = negotiation.RequestA;
     
     if (negotiation.PackageB != null)
     {
      event.TeamB = negotiation.PackageB.Team;
     }
     
     event.PackageB = negotiation.PackageB;
     event.RequestB = negotiation.RequestB;
     event.isCounterProposal = false;
     event.isModifiedProposal = false;
     event.isWithdrawnProposal = false;
     event.isTradeAccepted = (negotiation.Response == TradeResponse.Accept);
     event.isTradeFinished = true;
     event.TradeNegotiation = negotiation;

     event.Send();
    }
   }
  }
  // Otherwise, the trade was declined. End negotiations.
  else
  {
   EndTradeNegotiation(negotiation);

   // Send and event for the declined negotiation.
   Trade_TradeProposalEvent event = new Trade_TradeProposalEvent;
   if (event != null)
   {
    if (negotiation.PackageA != null)
    {
     event.TeamA = negotiation.PackageA.Team;
    }
    
    event.PackageA = negotiation.PackageA;
    event.RequestA = negotiation.RequestA;
    
    if (negotiation.PackageB != null)
    {
     event.TeamB = negotiation.PackageB.Team;
    }
    
    event.PackageB = negotiation.PackageB;
    event.RequestB = negotiation.RequestB;
    event.isCounterProposal = false;
    event.isModifiedProposal = false;
    event.isWithdrawnProposal = (request.Response == TradeNegotiation_WithdrawResponse);
    event.isTradeAccepted = false;
    event.isTradeFinished = true;
    event.TradeNegotiation = negotiation;

    event.Send();
   }
  }
 }
}

// If there are outstanding negotiations, process them all.
if ((TradeNegotiationList != null) && (TradeNegotiationList.Population() != 0))
{
    // Process all outstanding trade negotiations.
    UpdateTradeNegotiations view = new UpdateTradeNegotiations;
    {
        view.Subjects = TradeNegotiationList;
        view.TradeEvalRef = TradeEvalRef;
        view.TradeNegotiationManagerRef = this;
    }

    // Clear the negotiation list.
    TradeNegotiationList.EraseAll();
}

// Process the request.
if (NegotiationManager != null)
{
    NegotiationManager.ProcessNegotiationRequest(this);
}

// Clear the negotiation references.
NegotiationManager = null;
Negotiation = null;

// Set the transaction ID
entry.TransactionId = TransactionId;
TransactionId = TransactionId + 1;

if (TransactionList.Population() == TransactionList.Capacity())
{
    TransactionList[TransactionListBegin] = entry;
    TransactionListBegin = (TransactionListBegin + 1) % TransactionList.Capacity();
}
else
{
    TransactionList.Append(entry);
}

EditDraftClassTransactionHistoryEntry entry = new EditDraftClassTransactionHistoryEntry;
if (entry != null)
{
    entry.EditType = event.EditType;
    entry.GamerTag = event.GamerTag;
    entry.SeasonStage = event.SeasonStage;
 
    // If this is the offseason, use the stage (convert to zero-based).
    if (entry.SeasonStage == SeasonStage.OffSeason)
    {
        entry.SeasonWeek = SeasonInfo.CurrentOffseasonStage - 1;
    }
    else
    {
        entry.SeasonWeek = event.SeasonWeek;
    }
    
    entry.SeasonYear = event.SeasonYear;
 
    AppendTransaction(entry);
}

PlayerEditTransactionHistoryEntry entry = new PlayerEditTransactionHistoryEntry;
if (entry != null)
{
    entry.Description = event.Description;
    entry.EditDescription = event.EditDescription;
    entry.GamerTag = event.GamerTag; 
    entry.Title = event.Title;
    entry.SeasonStage = event.SeasonStage;

    // If this is the offseason, use the stage (convert to zero-based).
    if (entry.SeasonStage == SeasonStage.OffSeason)
    {
      entry.SeasonWeek = SeasonInfo.CurrentOffseasonStage - 1;
    }
    else
    {
      entry.SeasonWeek = event.SeasonWeek;
    }
    
    entry.SeasonYear = event.SeasonYear;
    entry.Player = event.Player;
    entry.OldTeam = event.Team;
    entry.NewTeam = event.Team;
    AppendTransaction(entry);
}

PlayerPositionChangeHistoryEntry entry = new PlayerPositionChangeHistoryEntry;
if (entry != null)
{
    // Initialize the date information.
    entry.SeasonYear = SeasonInfo.CurrentYear;
    entry.SeasonStage = SeasonInfo.CurrentStage;
    
    // EA_TODO("cwood","4/24/15","Fix season manager end week to handle pushing week past valid number in post release clean up");
    // If this is the offseason, use the stage (convert to zero-based).
    if (entry.SeasonStage == SeasonStage.OffSeason)
    {
        entry.SeasonWeek = SeasonInfo.CurrentOffseasonStage - 1;
    }
    else if (entry.SeasonStage == SeasonStage.PreSeason)
    {
        if (SeasonInfo.CurrentWeek >= SeasonInfo.PreseasonWeekCount)
        {
            entry.SeasonWeek = (SeasonInfo.PreseasonWeekCount - 1);
        }
        else
        {
            entry.SeasonWeek = SeasonInfo.CurrentWeek;
        }
    }
    else if (entry.SeasonStage == SeasonStage.NFLSeason)
    {
        if (SeasonInfo.CurrentWeek >= SeasonInfo.NflseasonWeekCount)
        {
            entry.SeasonWeek = (SeasonInfo.NflseasonWeekCount - 1);
        }
        else
        {
            entry.SeasonWeek = SeasonInfo.CurrentWeek;
        }
    }
    
    // Initialize the player information.
    entry.Player = event.Player;
    entry.OldTeam = event.Team;
    entry.OldPlayerType = event.OldPlayerType;
    entry.NewPlayerType = event.NewPlayerType;
    entry.OldPosition = event.OldPosition;
    entry.NewPosition = event.NewPosition;
    
    // Add the entry to the list.
    AppendTransaction(entry);
}

DraftPickTransactionHistoryEntry entry = new DraftPickTransactionHistoryEntry ;
if (entry != null)
{
    DraftPick pick = event.Pick;
    DraftPick nullPick = null;
    if (pick != nullPick)
    {
        // Initialize the date information.
        entry.SeasonYear = SeasonInfo.CurrentYear;
        entry.SeasonStage = SeasonInfo.CurrentStage;

        // If this is the offseason, use the stage (convert to zero-based).
        if (entry.SeasonStage == SeasonStage.OffSeason)
        {
            entry.SeasonWeek = SeasonInfo.CurrentOffseasonStage - 1;
        }
        // Otherwise, use the week number.
        else
        {
            entry.SeasonWeek = SeasonInfo.CurrentWeek;
        }

        // Initialize the team and player information.
        entry.OldTeam = event.OldTeam;
        entry.NewTeam = event.NewTeam;

        // Set Draft Pick information
        entry.Round = pick.Round;
        entry.Pick = pick.PickNumber;
        entry.Year = pick.YearOffset + SeasonInfo.CurrentSeasonYear;

        // Add the entry to the list.
        AppendTransaction(entry);
    }
}

StaffPersonContractStatus newContractStatus = event.NewContractStatus;

if ((newContractStatus == StaffPersonContractStatus.Signed) ||
    (newContractStatus == StaffPersonContractStatus.FreeAgent) ||
    (newContractStatus == StaffPersonContractStatus.Retired))
{
    CoachTransactionHistoryEntry entry = new CoachTransactionHistoryEntry;
    if (entry != null)
    {
        // Initialize the date information.
        entry.SeasonYear = SeasonInfo.CurrentYear;
        entry.SeasonStage = SeasonInfo.CurrentStage;

            // EA_TODO("cwood","4/24/15","Fix season manager end week to handle pushing week past valid number in post release clean up");
            // If this is the offseason, use the stage (convert to zero-based).
            if (entry.SeasonStage == SeasonStage.OffSeason)
            {
                entry.SeasonWeek = SeasonInfo.CurrentOffseasonStage - 1;
            }
            else if (entry.SeasonStage == SeasonStage.PreSeason)
            {
                if (SeasonInfo.CurrentWeek >= SeasonInfo.PreseasonWeekCount)
                {
                    entry.SeasonWeek = (SeasonInfo.PreseasonWeekCount - 1);
                }
                else
                {
                    entry.SeasonWeek = SeasonInfo.CurrentWeek;
                }
            }
            else if (entry.SeasonStage == SeasonStage.NFLSeason)
            {
                if (SeasonInfo.CurrentWeek >= SeasonInfo.NflseasonWeekCount)
                {
                    entry.SeasonWeek = (SeasonInfo.NflseasonWeekCount - 1);
                }
                else
                {
                    entry.SeasonWeek = SeasonInfo.CurrentWeek;
                }
            }

        // Initialize the team and player information.
        entry.OldTeam = event.OldTeam;
        entry.NewTeam = event.NewTeam;
        entry.Coach = event.Coach;

        // Initialize the contract information.
        entry.ContractStatus = event.NewContractStatus;
        entry.ContractLength = event.Coach.ContractLength;
        entry.ContractSalary = event.Coach.ContractSalary;

        // Add the entry to the list.
        AppendTransaction(entry);
    }
}

PlayerContractStatus newContractStatus = event.NewContractStatus;

if ((newContractStatus == PlayerContractStatus.Signed) ||
    (newContractStatus == PlayerContractStatus.Drafted) ||
    (newContractStatus == PlayerContractStatus.FreeAgent) ||
    (newContractStatus == PlayerContractStatus.Retired) ||
    (newContractStatus == PlayerContractStatus.PracticeSquad))
{
    PlayerTransactionHistoryEntry entry = new PlayerTransactionHistoryEntry;
    if (entry != null)
    {
        PlayerContractStatus oldContractStatus = event.OldContractStatus;
        if (!((oldContractStatus == PlayerContractStatus.Expiring) &&
              (newContractStatus == PlayerContractStatus.FreeAgent)))
        {
            // Initialize the date information.
            entry.SeasonYear = SeasonInfo.CurrentYear;
            entry.SeasonStage = SeasonInfo.CurrentStage;
            
            bool excludeContractYearZero = false;

            // EA_TODO("cwood","4/24/15","Fix season manager end week to handle pushing week past valid number in post release clean up");
            // If this is the offseason, use the stage (convert to zero-based).
            if (entry.SeasonStage == SeasonStage.OffSeason)
            {
                entry.SeasonWeek = SeasonInfo.CurrentOffseasonStage - 1;
            }
            else
            {
                if (entry.SeasonStage == SeasonStage.PreSeason)
                {
                    if (SeasonInfo.CurrentWeek >= SeasonInfo.PreseasonWeekCount)
                    {
                        entry.SeasonWeek = (SeasonInfo.PreseasonWeekCount - 1);
                    }
                    else
                    {
                        entry.SeasonWeek = SeasonInfo.CurrentWeek;
                    }
                }
                else if (entry.SeasonStage == SeasonStage.NFLSeason)
                {
                    if (SeasonInfo.CurrentWeek >= SeasonInfo.NflseasonWeekCount)
                    {
                        entry.SeasonWeek = (SeasonInfo.NflseasonWeekCount - 1);
                    }
                    else
                    {
                        entry.SeasonWeek = SeasonInfo.CurrentWeek;
                    }
                }
                if ((newContractStatus == PlayerContractStatus.Signed) && (event.OldTeam == event.NewTeam) && (event.OldContractStatus != PlayerContractStatus.PracticeSquad))
                {
                    // This is a re-sign transaction.  Do NOT include the player's contract year zero info.
                    // Year 0 contains the last year of the player's expiring contract.
                    excludeContractYearZero = true;
                }
            }

            // Initialize the team and player information.
            entry.OldTeam = event.OldTeam;
            entry.NewTeam = event.NewTeam;
            entry.Player = event.Player;

            Player player = event.Player;

            // Initialize the contract information.
            entry.ContractStatus = event.NewContractStatus;
            entry.OldContractStatus = event.OldContractStatus;
            
            entry.ContractLength = event.Player.ContractLength;
            entry.ContractTotalSalary = player.ContractSalary0 + player.ContractBonus0 +
                                        player.ContractSalary1 + player.ContractBonus1 +
                                        player.ContractSalary2 + player.ContractBonus2 +
                                        player.ContractSalary3 + player.ContractBonus3 +
                                        player.ContractSalary4 + player.ContractBonus4 +
                                        player.ContractSalary5 + player.ContractBonus5 +
                                        player.ContractSalary6 + player.ContractBonus6 +
                                        player.ContractSalary7 + player.ContractBonus7;
            entry.ContractSalary = player.ContractSalary0 + player.ContractSalary1 +
                                   player.ContractSalary2 + player.ContractSalary3 +
                                   player.ContractSalary4 + player.ContractSalary5 +
                                   player.ContractSalary6 + player.ContractSalary7;
            entry.ContractBonus = player.ContractBonus0 + player.ContractBonus1 + player.ContractBonus2 +
                                  player.ContractBonus3 + player.ContractBonus4 + player.ContractBonus5 +
                                  player.ContractBonus6 + player.ContractBonus7;
            
            if (excludeContractYearZero)
            {
                // Remove all values from contract year zero since it's part of a different (expiring) contract.
                entry.ContractLength = entry.ContractLength - 1;
                entry.ContractTotalSalary = entry.ContractTotalSalary - (player.ContractSalary0 + player.ContractBonus0);
                entry.ContractSalary = entry.ContractSalary - player.ContractSalary0;
                entry.ContractBonus = entry.ContractBonus - player.ContractBonus0;
            }

            // Add the entry to the list.
            AppendTransaction(entry);
        }
    }
}

int comeBackChance = Subject.ComebackChance;
int percentTotal = 100;
int randomOutCome = RandomRef.GetRandomIntRange(0, percentTotal);

if(randomOutCome <= comeBackChance)
{
    Player player = Subject.Player;
    PlayerRetirementEvalRef.HandleComebackPlayer(player);

    PlayerComebackEvent event = new PlayerComebackEvent;
    event.Player = player;
    event.Send();

    PlayerComebackList.FindAndErase(Subject);
}

//Choose whether to withdraw the offer, stay with the current offer, or up the value of the offer
int decision = Env.PlayerSigningEvalRef.RandomRef.GetRandomIntRange(0, 100);

//If the offer is being withdrawn add the player to the team's offer blacklist
if(decision < Env.TeamRef.Philosophy.BuildingPhilosophy.FreeAgentOfferWithdrawPercent)
{
  Env.TeamRef.ContractOfferBlacklist.Append(Subject.Player);
  Env.PlayerSigningEvalRef.OutstandingOffers.FindAndErase(Subject);
  Env.PlayerSigningEvalRef.LogTeamDecision(Subject.Team, Subject.Player, TeamDecision.Withdraw);
}

Subject.TeamDecision = decision;

int decision = Subject.TeamDecision - Env.TeamRef.Philosophy.BuildingPhilosophy.FreeAgentOfferWithdrawPercent;

if(decision < Env.TeamRef.Philosophy.BuildingPhilosophy.FreeAgentOfferStayPercent)
{
  //Stay with the current Offer
  Env.PlayerSigningEvalRef.LogTeamDecision(Subject.Team, Subject.Player, TeamDecision.Wait);
}
else
{
  //Up the offer based on the team's spending philosophy
  Env.PlayerSigningEvalRef.IncreaseContractOffer(Subject, Env.TeamRef.Philosophy.SpendingPhilosophy);
  Env.PlayerSigningEvalRef.LogTeamDecision(Subject.Team, Subject.Player, TeamDecision.Increase);
}

if(decision < 0)
{
  decision = 0;
}

Subject.TeamDecision = decision;
Subject.Declined = false;

Team team = Subject;
Coach coach = team.HeadCoach;

// If the coach is valid, update his career winning season based on the team record.
if (coach != null)
{
    if ((team.ConfWin + team.NonConfWin) > (team.ConfLoss + team.NonConfLoss))
    {
        coach.CareerWinSeasons = coach.CareerWinSeasons+ 1;
    }
}

SeasonGame game = Subject;

// If this is a playoff or Pro Bowl bowl week, update both team coach's stats.
if ( ( (game.SeasonWeekType >= SeasonWeekType.FirstPlayoff_) && (game.SeasonWeekType <= SeasonWeekType.LastPlayoff_) ) ||
     ( game.SeasonWeekType == SeasonWeekType.ProBowl ) )
{
    Team homeTeam = game.HomeTeam;
    Team awayTeam = game.AwayTeam;

    if (homeTeam != null)
    {
        Coach homeCoach = homeTeam.HeadCoach;
        if (homeCoach != null)
        {
            homeCoach.CareerPlayoffsMade = homeCoach.CareerPlayoffsMade + 1;
        }
    }

    if (awayTeam != null)
    {
        Coach awayCoach = awayTeam.HeadCoach;
        if (awayCoach != null)
        {
            awayCoach.CareerPlayoffsMade = awayCoach.CareerPlayoffsMade + 1;
        }
    }
}

PlayerReSignNegotiation negotiation = Subject;
Team team = negotiation.Team;
Player player = negotiation.Player;

// Find the depth chart depth for this player.
int depth = team.DepthChart.GetPlayerDepth(player, player.Position);

// If the player does not yet have a contract, generate it.
if (negotiation.ContractLength == 0)
{
    // Generate the market value contract.
    PlayerBaseContract marketContract = PlayerSigningEval.GenerateMarketValueContract(player);
    
    if (marketContract != null)
    {
        negotiation.ContractSalary = marketContract.Salary;
        negotiation.ContractBonus = marketContract.Bonus;
        negotiation.ContractLength = marketContract.Length;
        
        negotiation.FairMarketContractSalary = marketContract.Salary;
        negotiation.FairMarketContractBonus = marketContract.Bonus;
        negotiation.FairMarketContractLength = marketContract.Length;
        
        int contractVarianceRoll = ReSignNegotiationManager.Random.GetRandomIntRange(0, 100);
        int contractVariancePercent = ReSignNegotiationManager.DesiredContractVarianceSpline.CalculateY(contractVarianceRoll);
        
        negotiation.ActualDesiredContractSalary = marketContract.Salary + ((marketContract.Salary * contractVariancePercent ) / 100);
        negotiation.ActualDesiredContractBonus = marketContract.Bonus + ((marketContract.Bonus * contractVariancePercent ) / 100);
        negotiation.ActualDesiredContractLength = marketContract.Length;
        negotiation.NegotiationCount = 0;
    
        negotiation.InitialContract = true;
    }
    else
    {
        negotiation.ContractSalary = 0;
        negotiation.ContractBonus = 0;
        negotiation.ContractLength = 0;
        
        negotiation.FairMarketContractSalary = 0;
        negotiation.FairMarketContractBonus = 0;
        negotiation.FairMarketContractLength = 0;
        
        negotiation.ActualDesiredContractSalary = 0;
        negotiation.ActualDesiredContractBonus = 0;
        negotiation.ActualDesiredContractLength = 0;
        negotiation.NegotiationCount = 0;
    }
}

// If the player has already been re-signed, end the negotiation and remove it.
if (negotiation.ReSignStatus == ReSignStatus.ReSigned)
{
    negotiation.NegotiationsEnded = true;

    // Create the contract response.
    ContractResponse contractResponse = new ContractResponse;
    contractResponse.Player = player;
    contractResponse.Accepted = true;
    contractResponse.ContractLength = negotiation.ContractLength;
    contractResponse.ContractSalary = negotiation.ContractSalary;
    contractResponse.ContractBonus = negotiation.ContractBonus;

    // Create and issue a accept notification.
    ReSign_PlayerAcceptedRequest request = PlayerSigningEval.CreatePlayerOfferAcceptedRequest();
    if (request != null)
    {
        request.RequestData = contractResponse;
        TeamRequestManager.Issue(team, request );
    }

    // Erase the negotiation from the list.
    PlayerSigningEval.PlayerReSignList.FindAndErase( negotiation );
}
// If the player is unavailable, end the negotiation and remove it.
else if (negotiation.ReSignStatus == ReSignStatus.NotInterested)
{
    negotiation.NegotiationsEnded = true;

    // Create the contract response.
    ContractResponse contractResponse = new ContractResponse;
    contractResponse.Player = player;
    contractResponse.Accepted = false;
    contractResponse.ContractLength = negotiation.ContractLength;
    contractResponse.ContractSalary = negotiation.ContractSalary;
    contractResponse.ContractBonus = negotiation.ContractBonus;

    // Create and issue the decline notification.
    ReSign_PlayerDeclinedRequest request = PlayerSigningEval.CreatePlayerOfferDeclinedRequest();
    if (request != null)
    {
        request.RequestData = contractResponse;
        TeamRequestManager.Issue(team, request);
    }
}
// If the player is still signed, but has not re-signed, send the team request.
else if ( (player.ContractStatus == PlayerContractStatus.Signed) || (player.ContractStatus == PlayerContractStatus.Expiring) )
{
    if (negotiation.ReSignStatus < ReSignStatus.NegotiatingBegin || negotiation.ReSignStatus > ReSignStatus.NegotiatingEnd)
    {
        negotiation.ReSignStatus =  ReSignStatus.StartNegotiations;
    }

    // If the team requests flag is set, proceed. Otherwise, this view is only being called to
    // process player responses.
    if (SendTeamRequests)
    {
        // If the team can offer the player, determine their interest. Note that we don't check the
        // spending money if it is not the initial offer, since we assume the previous offer was
        // already validated.
        if (!negotiation.InitialContract || PlayerSigningEval.CanTeamOfferReSignPlayer(team, player, negotiation.ContractLength, negotiation.ContractSalary, negotiation.ContractBonus))
        {
            negotiation.TeamReSignInterest = PlayerSigningEval.TeamReSignInterest(team, player);
        }
        // Otherwise, the team is not interested.
        else
        {
            negotiation.TeamReSignInterest = 0;
        }

        // Create the re-sign request, but only if it is delegated.  It should not be visible to the user.
        if (DelegateTeamRequest)
        {
            ReSign_TeamRequest teamRequest = PlayerSigningEval.CreateTeamRequest(negotiation.ContractLength, negotiation.ContractSalary, negotiation.ContractBonus);
            if (teamRequest != null)
            {
                teamRequest.Player = player;
                teamRequest.Team = team;
                teamRequest.PlayerDesiredSalary = negotiation.FairMarketContractSalary;
                teamRequest.PlayerDesiredLength = negotiation.FairMarketContractLength;
                teamRequest.PlayerDesiredBonus = negotiation.FairMarketContractBonus;
                teamRequest.TeamReSignInterest = negotiation.TeamReSignInterest;
                teamRequest.PlayerReSignInterest = negotiation.PlayerReSignInterest;
                teamRequest.RequestData = team;
                teamRequest.PlayerReSignNegotiation = negotiation;
                teamRequest.ReSignNegotiationManager = ReSignNegotiationManager;
                teamRequest.IsInitialOffer = negotiation.InitialContract;
                
                teamRequest.Response = teamRequest.DefaultResponse;
                teamRequest.HandleResponse();
            }
        }
    }
}
// Otherwise, the negotiation must end.
else
{
    negotiation.NegotiationsEnded = true;
}

Team team = Subject;

// If the clear offer reserves flag is set, do so.
if (ClearOfferReserves)
{
    team.SalCapThisYearOfferCount = 0;
    team.SalCapThisYearOfferReserve = 0;
    team.SalCapNextYearOfferCount = 0;
    team.SalCapNextYearOfferReserve = 0;
}

// Update the team salary info cache.
SalaryCapEvalRef.UpdateTeamSalaryInfoCache(team);

Team team = Subject;

// Update the team philosophy on the team roadmap.
if (team.Roadmap != null)
{
    team.Roadmap.TeamPhilosophy = team.Philosophy;
}

UpdatePlayerGrades updatePlayerGrades = new UpdatePlayerGrades;
updatePlayerGrades.Subjects = Subject.Roster;
updatePlayerGrades.PlayerGradeEval = GradeEvalModule;
updatePlayerGrades.Team = Subject;
updatePlayerGrades.Update();

ClearInjuredReserveForPlayers clearRosterIRView = new ClearInjuredReserveForPlayers;
clearRosterIRView.Subjects = Subject.Roster;
clearRosterIRView.Update();

clearRosterIRView = null;

Subject.ActiveRosterSize = Subject.Roster.Population();

if (ScoutingModule.SeasonInfo.CurrentWeek >= ScoutingModule.ScoutingPeriodStartWeek)
{
    Subject.ScoutingPoints = ((Subject.ScoutingPoints * ScoutingModule.ScoutingDecayPercent) / 100);

    if(Subject.HeadCoach != null)
    {
        Coach headCoach = Subject.HeadCoach;
        if(Subject.HeadCoach.TraitExpertScout == true) 
        {
            Subject.ScoutingPoints = Subject.ScoutingPoints + (PointsPerWeekCoachBonus * CoachTraitModSpline.CalculateY(headCoach.Level)) / 100;
        }
        else 
        {
            Subject.ScoutingPoints = Subject.ScoutingPoints + (PointsPerWeek * CoachTraitModSpline.CalculateY(headCoach.Level)) / 100;
        }
    }
    else
    {
        Subject.ScoutingPoints = Subject.ScoutingPoints + PointsPerWeek;
    }

    Scouting_ScoutingPointsRequest request = ScoutingModule.CreateTeamRequest(ScoutingPeriod, Subject);
    TeamRequestManager.Issue(Subject,request);
}

TradeNegotiation negotiation = Subject;

// If the negotiation is delegated, perform CPU logic to select the best package.
if (negotiation.IsDelegated)
{
    // Evaluate the offers and select the best package.
    negotiation.PackageB = TradeEvalRef.EvaluateTradeOffers(negotiation.PackageA, negotiation.TeamTradePackageListB);

    // If a package was selected, the trade was accepted.
    if (negotiation.PackageB != null)
    {
        negotiation.Response = TradeResponse.Accept;
    }
    // Otherwise, the trade was declined.
    else
    {
        negotiation.Response = TradeResponse.Decline;
    }
}

// End the trade negotiation.
TradeNegotiationManagerRef.EndTradeNegotiation(negotiation);

int returnVal = 0;

int progress = GetProgress(coach, team, teamSeasonStats);

if (progress >= CompletionValue[4])
{
    returnVal = 4;
}
else if (progress >= CompletionValue[3])
{
    returnVal = 3;
}
else if (progress >= CompletionValue[2])
{
    returnVal = 2;
}
else if (progress >= CompletionValue[1])
{
    returnVal = 1;
}
else
{
    returnVal = 0;
}

returnVal;

int completedLevel = GetCompletedLevel(coach, team, teamSeasonStats);

int progressPercentage = 25 * completedLevel;

if (progressPercentage > 100) 
{
    progressPercentage = 100;
}

progressPercentage;

int returnVal = 0;

int progress = GetProgress(player, team, playerSeasonStats, teamSeasonStats, evalInfo);

if (progress >= CompletionValue[4])
{
    returnVal = 4;
}
else if (progress >= CompletionValue[3])
{
    returnVal = 3;
}
else if (progress >= CompletionValue[2])
{
    returnVal = 2;
}
else if (progress >= CompletionValue[1])
{
    returnVal = 1;
}

returnVal;

int progress = GetProgress(player, team, playerSeasonStats, teamSeasonStats, evalInfo );
int progressPercentage = (100 * progress)/ CompletionValue[4];

if (progressPercentage > 100) 
{
    progressPercentage = 100;
}

progressPercentage;

TeamStats subjectASeasonStats = Subject.GetTeamCurrentSeasonStats();
TeamStats subjectBSeasonStats = OtherSubject.GetTeamCurrentSeasonStats();

(subjectASeasonStats.DEFPASSYARDS + subjectASeasonStats.DEFRUSHYARDS) - (subjectBSeasonStats.DEFPASSYARDS + subjectBSeasonStats.DEFRUSHYARDS);

TeamStats subjectASeasonStats = OtherSubject.GetTeamCurrentSeasonStats();
TeamStats subjectBSeasonStats = Subject.GetTeamCurrentSeasonStats();

(subjectASeasonStats.OFFPASSYARDS + subjectASeasonStats.OFFRUSHYARDS) - (subjectBSeasonStats.OFFPASSYARDS + subjectBSeasonStats.OFFRUSHYARDS);
